import { Check, X } from "lucide-react";
import '../utils/style/PasswordStrengthMeter.css';

const PasswordCriteria = ({ password }) => {
	const criteria = [
		{ label: "At least 8 characters", met: password.length >= 8 },
		{ label: "Contains uppercase letter", met: /[A-Z]/.test(password) },
		{ label: "Contains lowercase letter", met: /[a-z]/.test(password) },
		{ label: "Contains a number", met: /\d/.test(password) },
		{ label: "Contains special character", met: /[^A-Za-z0-9]/.test(password) },
	];

	return (
		<div className='password-criteria'>
			{criteria.map((item) => (
				<div key={item.label} className='criteria-item'>
					{item.met ? (
						<Check className='icon check' />
					) : (
						<X className='icon x' />
					)}
					<span className={item.met ? "strong-text" : "text-gray-500"}>{item.label}</span>
				</div>
			))}
		</div>
	);
};

const PasswordStrengthMeter = ({ password }) => {
	const getStrength = (pass) => {
		let strength = 0;
		if (pass.length >= 8) strength++;
		if (pass.match(/[a-z]/) && pass.match(/[A-Z]/)) strength++;
		if (pass.match(/\d/)) strength++;
		if (pass.match(/[^a-zA-Z\d]/)) strength++;
		return strength;
	};

	const strength = getStrength(password);

	const getColorClass = (index) => {
		if (index < strength) {
			if (index < 1) return "weak";
			if (index < 2) return "fair";
			if (index < 3) return "good";
			return "strong";
		}
		return "inactive";
	};

	const getStrengthText = (strength) => {
		if (strength === 0) return "Very Weak";
		if (strength === 1) return "Weak";
		if (strength === 2) return "Fair";
		if (strength === 3) return "Good";
		return "Strong";
	};

	return (
		<div className='meter'>
			<div className='meter-header'>
				<span>Password Strength</span>
				<span className={`text-sm font-medium ${strength >= 3 ? "strong-text" : strength >= 2 ? "text-yellow-600" : "weak-text"}`}>
					{getStrengthText(strength)}
				</span>
			</div>

			<div className='meter-bar'>
				{[...Array(5)].map((_, index) => (
					<div
						key={index}
						className={`h-2 w-1/5 rounded-full ${getColorClass(index)}`}
					/>
				))}
			</div>
			<PasswordCriteria password={password} />
		</div>
	);
};

export default PasswordStrengthMeter;
