import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils';

function Signup() {
    const [signupInfo, setSignupInfo] = useState({
        first_name: '',
        last_name: '',
        email: '',
        phone: '',
        password: '',
        dob: {
            day: '',
            month: '',
            year: ''
        }
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        const copySignupInfo = { ...signupInfo };
        if (name === 'day' || name === 'month' || name === 'year') {
            copySignupInfo.dob[name] = value;
        } else {
            copySignupInfo[name] = value;
        }
        setSignupInfo(copySignupInfo);
    };

    const handleSignup = async (e) => {
        e.preventDefault();
        const { first_name, last_name, email, phone, password, dob } = signupInfo;
        if (!first_name || !last_name || !email || !phone || !password || !dob.day || !dob.month || !dob.year) {
            return handleError('All fields are required');
        }
        try {
            const url = `http://localhost:8080/auth/signup`;
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify({
                    ...signupInfo,
                    dob: new Date(`${dob.year}-${dob.month}-${dob.day}`)
                })
            });
            const result = await response.json();
            const { success, message, error } = result;
            if (success) {
                handleSuccess(message);
                setTimeout(() => {
                    navigate('/login');
                }, 1000);
            } else if (error) {
                const details = error?.details[0].message;
                handleError(details);
            } else {
                handleError(message);
            }
        } catch (err) {
            handleError(err);
        }
    };

    return (
        <div className='container'>
            <h1>Signup</h1>
            <form onSubmit={handleSignup}>
                <div>
                    <label htmlFor='first_name'>First Name</label>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='first_name'
                        placeholder='Enter your first name...'
                        value={signupInfo.first_name}
                    />
                </div>
                <div>
                    <label htmlFor='last_name'>Last Name</label>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='last_name'
                        placeholder='Enter your last name...'
                        value={signupInfo.last_name}
                    />
                </div>
                <div>
                    <label htmlFor='email'>Email</label>
                    <input
                        onChange={handleChange}
                        type='email'
                        name='email'
                        placeholder='Enter your email...'
                        value={signupInfo.email}
                    />
                </div>
                <div>
                    <label htmlFor='phone'>Phone</label>
                    <input
                        onChange={handleChange}
                        type='text'
                        name='phone'
                        placeholder='Enter your phone number...'
                        value={signupInfo.phone}
                    />
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input
                        onChange={handleChange}
                        type='password'
                        name='password'
                        placeholder='Enter your password...'
                        value={signupInfo.password}
                    />
                </div>
                <div>
                    <label htmlFor='password'>Password</label>
                    <input
                        onChange={handleChange}
                        type='password'
                        name='password'
                        placeholder='Enter your password...'
                        value={signupInfo.password}
                    />
                </div>
                
                <div>
                    <label htmlFor='dob'>Date of Birth</label>
                    <div>
                        <select name='day' onChange={handleChange} value={signupInfo.dob.day}>
                            <option value=''>Day</option>
                            {[...Array(31)].map((_, index) => (
                                <option key={index} value={index + 1}>{index + 1}</option>
                            ))}
                        </select>
                        <select name='month' onChange={handleChange} value={signupInfo.dob.month}>
                            <option value=''>Month</option>
                            {[...Array(12)].map((_, index) => (
                                <option key={index} value={index + 1}>{index + 1}</option>
                            ))}
                        </select>
                        <select name='year' onChange={handleChange} value={signupInfo.dob.year}>
                            <option value=''>Year</option>
                            {[...Array(121)].map((_, index) => (
                                <option key={index} value={new Date().getFullYear() - index}>{new Date().getFullYear() - index}</option>
                            ))}
                        </select>
                    </div>
                </div>
                <button type='submit'>Signup</button>
                <ToastContainer />
            </form>
            <p>
                Already have an account? <Link to='/login'>Login here</Link>
            </p>
        </div>
    );
}

export default Signup;
