import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/SentOtpForgotPassword.css';
import OtpInput from '../../Services/OtpInput'; // Updated import path

function SentOtpForgotPassword() {
    const [otp, setOtp] = useState('');
    const [timer, setTimer] = useState(60);
    const [isResendEnabled, setIsResendEnabled] = useState(false);
    const [isComplete, setIsComplete] = useState(false);
    const [hasCodeBeenSent, setHasCodeBeenSent] = useState(false); // New state to track code sending
    const { state } = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        // Check local storage to see if code has been sent before
        const codeSentStatus = localStorage.getItem('codeSentStatus');
        if (codeSentStatus === 'true') {
            setHasCodeBeenSent(true);
        }

        // Timer logic
        const countdown = timer > 0 ? setInterval(() => {
            setTimer((prev) => prev - 1);
        }, 1000) : null;

        if (timer === 0) {
            setIsResendEnabled(true);
        }

        return () => {
            if (countdown) clearInterval(countdown);
        };
    }, [timer]);

    useEffect(() => {
        // If code has been sent, persist timer state
        if (hasCodeBeenSent) {
            setTimer(parseInt(localStorage.getItem('timer') || '60'));
        }
    }, [hasCodeBeenSent]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/verification/forgot-password/verify-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email, otp }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                navigate('/forgot-password/reset-password', { state: { email: state.email } });
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    const handleSendOtp = async () => {
        try {
            setHasCodeBeenSent(true); // Update state
            localStorage.setItem('codeSentStatus', 'true'); // Store code sent status in local storage

            // Reset timer
            setTimer(60);
            localStorage.setItem('timer', '60'); // Store initial timer value

            const response = await fetch('http://localhost:8080/verification/forgot-password/send-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Failed to send OTP. Please try again.');
        }
    };

    const handleResendOtp = async () => {
        try {
            setIsResendEnabled(false);
            setTimer(60); // Reset timer
            localStorage.setItem('timer', '60'); // Update timer value in local storage

            const response = await fetch('http://localhost:8080/verification/forgot-password/send-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Failed to resend OTP. Please try again.');
        }
    };

    return (
        <div className="otp-wrapper">
            <div>
                <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            </div>
            <h1 className="otp-title">Verification Code</h1>
            <div className="otp-timer">
                {hasCodeBeenSent ? (
                    <p>We've sent a verification code to your registered email address.</p>
                ) : (
                    <p>Click the button to send the verification code to your email.</p>
                )}
                <div className="timer">
                    <p>Time Remaining: {timer} s</p>
                </div>
            </div>
            <form onSubmit={handleSubmit}>
                {!hasCodeBeenSent ? (
                    <button
                        type="button"
                        className="otp-btn send"
                        onClick={handleSendOtp}
                    >
                        Send Code
                    </button>
                ) : (
                    <OtpInput
                        length={6}
                        onOtpSubmit={(otp) => setOtp(otp)}
                        onOtpComplete={(complete) => setIsComplete(complete)}
                    />
                )}
                <div className="otp-buttons">
                    {timer === 0 ? (
                        <>
                            <div className="otp-massage">
                                <p>Didn't receive the code?</p>
                            </div>
                            <button
                                type="button"
                                className="otp-btn resend"
                                onClick={handleResendOtp}
                            >
                                Resend Code
                            </button>
                        </>
                    ) : hasCodeBeenSent ? (
                        <>
                            <div className="otp-massage">
                                <br />
                            </div>
                            <button
                                type="submit"
                                className={`otp-btn verify ${isComplete ? 'active' : ''}`}
                                disabled={!isComplete}
                            >
                                Verify Code
                            </button>
                        </>
                    ) : null}
                </div>
            </form>
            <ToastContainer />
        </div>
    );
}

export default SentOtpForgotPassword;
