import React, { useState, useRef } from "react";
import "../assets/style/ExtensionStyle/AvatarUploadPopup.css";
import { uploadAvatar } from "../utils/ApiService";
import LoadingOverlay from "../components/LoadingOverlay";

const AvatarUploadPopup = ({ onClose, onUploadSuccess }) => {
  const fileInputRef = useRef(null);
  const [loading, setLoading] = useState(false);
  const [tempAvatarUrl, setTempAvatarUrl] = useState(null); // State for temporary avatar URL

  const handleUploadClick = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (file) {
      // Create a temporary URL for the selected file
      const tempUrl = URL.createObjectURL(file);
      setTempAvatarUrl(tempUrl);

      setLoading(true);
      try {
        await uploadAvatar(file);
        onUploadSuccess();
        onClose();
      } catch (error) {
        console.error("Upload failed:", error);
      } finally {
        setLoading(false);
        // Revoke the temporary URL to free up memory
        URL.revokeObjectURL(tempUrl);
      }
    }
  };

  return (
    <div className="avatar-upload-popup-overlay">
      <div className="avatar-upload-popup">
        <LoadingOverlay loading={loading} />
        <p className="popup-title">Change Avatar</p>
        <div className="popup-options">
          {tempAvatarUrl && (
            <img
              src={tempAvatarUrl}
              alt="Temporary Avatar"
              className="temporary-avatar"
            />
          )}
          <button className="popup-option-button upload-button" onClick={handleUploadClick}>
            Select File
          </button>
          <input
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            className="file-input"
            ref={fileInputRef}
            style={{ display: "none" }} // Hide the input element
          />
          <button className="popup-option-button remove-button">Remove Avatar</button>
           {/* <button className="popup-option-button">Select Avatar</button> */}
          <button className="popup-option-button cancel-button" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default AvatarUploadPopup;
