import React from 'react';

function Notifications({ notifications, handleNotificationChange, showNotifications, toggleNotifications }) {
  return (
    <div className="details-container">
      <button className="expand-button" onClick={toggleNotifications}>
        {showNotifications ? "Hide" : "Show"} Notifications
      </button>
      {showNotifications && (
        <ul className="user-details">
          <li>
            <label>
              <input
                type="checkbox"
                checked={notifications.email}
                onChange={() => handleNotificationChange("email")}
              />
              Email Notifications
            </label>
          </li>
          <li>
            <label>
              <input
                type="checkbox"
                checked={notifications.push}
                onChange={() => handleNotificationChange("push")}
              />
              Push Notifications
            </label>
          </li>
          <li>
            <label>
              <input
                type="checkbox"
                checked={notifications.sms}
                onChange={() => handleNotificationChange("sms")}
              />
              SMS Notifications
            </label>
          </li>
        </ul>
      )}
    </div>
  );
}

export default Notifications;
