//ForgotPassword.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function ForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [userDetails, setUserDetails] = useState(null);
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);

    const navigate = useNavigate();

    useEffect(() => {
        const timeoutId = setTimeout(() => {
            setFadeIn(false);
        }, 500);
        return () => clearTimeout(timeoutId);
    }, []);

    const handleSendOTP = async () => {
        setLoading(true);
        try {
            const response = await fetch("http://localhost:8080/auth/forgot-password", {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ emailOrUsername })
            });

            const result = await response.json();

            if (response.ok) {
                setUserDetails({ name: result.name, avatar: result.avatar });
                handleSuccess('OTP sent successfully!');
            } else {
                handleError(result.message || 'User not found. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className={`login-container container ${fadeIn ? 'fade-in' : ''}`}>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Forgot Password</h1>
            <div className="form-group">
                <input
                    type="text"
                    id="emailOrUsername"
                    placeholder="Email or Username"
                    value={emailOrUsername}
                    onChange={(e) => setEmailOrUsername(e.target.value)}
                    required
                />
                <label htmlFor="emailOrUsername" className="form-label">Email or Username</label>
            </div>
            {userDetails && (
                <div className="user-details">
                    <img src={userDetails.avatar} alt="User Avatar" className="avatar" />
                    <h3>{userDetails.name}</h3>
                </div>
            )}
            <button onClick={handleSendOTP} disabled={loading}>
                {loading ? 'Sending OTP..' : 'Send OTP'}
            </button>
            <ToastContainer />
        </div>
    );
}

export default ForgotPassword;
