import React from 'react';
import { Link } from 'react-router-dom';

function ConfirmAccountPopup({ email, token }) {
    return (
        <div className="confirm-account-bar">
            <span className="confirm-account-message">
                Confirm your account otherwise it will be auto-deleted after 15 days.
            </span>
            <Link 
                to="/verify-email" 
                className="confirm-account-link" 
                state={{ 
                    token: token,
                    email: email
                }}
            >
                <button className="confirm-email-button">
                    Confirm Email
                </button>
            </Link>
        </div>
    );
}

export default ConfirmAccountPopup;
