// frontend/src/pages/UserProfile.js

import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/profile.css';
import UserInfo from './UserInfo';
import AccountSettings from './AccountSettings';
import Notifications from './Notifications';
import Privacy from './Privacy';
import DataStorage from './DataStorage';
import Appearance from './Appearance';

function UserProfile() {
  const [user, setUser] = useState({});
  const [loading, setLoading] = useState(true);
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [editProfile, setEditProfile] = useState(false);
  const [notifications, setNotifications] = useState({
    email: true,
    push: true,
    sms: false,
  });

  // State to manage the visibility of different sections
  const [showUserInfo, setShowUserInfo] = useState(false);
  const [showAccountSettings, setShowAccountSettings] = useState(false);
  const [showNotifications, setShowNotifications] = useState(false);
  const [showPrivacy, setShowPrivacy] = useState(false);
  const [showDataStorage, setShowDataStorage] = useState(false);
  const [showAppearance, setShowAppearance] = useState(false);

  const navigate = useNavigate();

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    setLoading(true);
    try {
      const response = await fetch("http://localhost:8080/Users/me", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      const result = await response.json();
      if (response.ok) {
        setUser(result);
      } else {
        handleError(
          result.message || "Failed to fetch user data. Please try again."
        );
        navigate("/login");
      }
    } catch (err) {
      handleError("Network error. Please check your connection and try again.");
      navigate("/login");
    } finally {
      setLoading(false);
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:8080/auth/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          refreshToken: localStorage.getItem("refreshToken"),
        }),
      });
      if (response.ok) {
        localStorage.removeItem("token");
        localStorage.removeItem("refreshToken");
        localStorage.removeItem("loggedInUser");
        handleSuccess("Logged out successfully! Redirecting...");
        setTimeout(() => navigate("/login"), 500);
      } else {
        handleError("Failed to log out. Please try again.");
      }
    } catch (err) {
      handleError("Network error. Please check your connection and try again.");
    }
  };

  const openLogoutModal = () => {
    setShowLogoutModal(true);
  };

  const closeLogoutModal = () => {
    setShowLogoutModal(false);
  };

  const toggleDarkMode = () => {
    setDarkMode(!darkMode);
  };

  const handleNotificationChange = (type) => {
    setNotifications((prev) => ({
      ...prev,
      [type]: !prev[type],
    }));
  };

  return (
    <div className={`profile-container ${darkMode ? "dark-mode" : ""}`}>
      {loading ? (
        <div className="loading-overlay">
          <img
            src="/apple-loading.gif"
            alt="Loading..."
            className="loading-spinner"
          />
        </div>
      ) : (
        <>
          <button
            className="back-button icon-button"
            onClick={() => navigate(-1)}
          >
            <img src="/back-icon.png" alt="Back" />
          </button>
          <button
            className="edit-button icon-button"
            onClick={() => setEditProfile(true)}
          >
            <img src="/edit-icon.png" alt="Edit" />
          </button>
          <div className="user-info">
            <div className="avatar">
              <img
                src="/avater.png"
                className="profile-picture"
                alt="Profile"
              />
            </div>
            <h1 className="user-name">{user.name}</h1>
          </div>

          {/* User Information */}
          <div className="details-container">
            <button
              className="expand-button"
              onClick={() => setShowUserInfo(!showUserInfo)}
            >
              {showUserInfo ? "Hide" : "Show"} User Information
            </button>
            {showUserInfo && (
              <ul className="user-details">
                <li data-label="Name:">{user.name}</li>
                <li data-label="Email:">{user.email}</li>
                <li data-label="Username:">{user.username}</li>
                <li data-label="Joined:">
                  {new Date(user.createdAt).toLocaleDateString()}
                </li>
                <li data-label="Phone:">{user.phone || "N/A"}</li>
                <li data-label="Address:">{user.address || "N/A"}</li>
              </ul>
            )}
          </div>

          {/* Account Settings */}
          <div className="details-container">
            <button
              className="expand-button highlight"
              onClick={() => setShowAccountSettings(!showAccountSettings)}
            >
              {showAccountSettings ? "Hide" : "Show"} Account Settings
            </button>

            {showAccountSettings && (
              <ul className="user-details">
                <li>Change Password</li>
                <li>Update Email</li>
                <li>Manage Two-Factor Authentication</li>
                <li>Delete Account</li>
              </ul>
            )}
          </div>

          {/* Notifications */}
          <div className="details-container">
            <button
              className="expand-button"
              onClick={() => setShowNotifications(!showNotifications)}
            >
              {showNotifications ? "Hide" : "Show"} Notifications
            </button>
            {showNotifications && (
              <ul className="user-details">
                <li>
                  <label>
                    <input
                      type="checkbox"
                      checked={notifications.email}
                      onChange={() => handleNotificationChange("email")}
                    />
                    Email Notifications
                  </label>
                </li>
                <li>
                  <label>
                    <input
                      type="checkbox"
                      checked={notifications.push}
                      onChange={() => handleNotificationChange("push")}
                    />
                    Push Notifications
                  </label>
                </li>
                <li>
                  <label>
                    <input
                      type="checkbox"
                      checked={notifications.sms}
                      onChange={() => handleNotificationChange("sms")}
                    />
                    SMS Notifications
                  </label>
                </li>
              </ul>
            )}
          </div>

          {/* Privacy */}
          <div className="details-container">
            <button
              className="expand-button"
              onClick={() => setShowPrivacy(!showPrivacy)}
            >
              {showPrivacy ? "Hide" : "Show"} Privacy
            </button>
            {showPrivacy && (
              <ul className="user-details">
                <li>Manage Blocked Users</li>
                <li>Privacy Settings</li>
                <li>Data Sharing Preferences</li>
                <li>Two-Factor Authentication Status</li>
              </ul>
            )}
          </div>

          {/* Data & Storage */}
          <div className="details-container">
            <button
              className="expand-button"
              onClick={() => setShowDataStorage(!showDataStorage)}
            >
              {showDataStorage ? "Hide" : "Show"} Data & Storage
            </button>
            {showDataStorage && (
              <ul className="user-details">
                <li>Data Usage</li>
                <li>Storage Space</li>
                <li>Download Your Data</li>
                <li>Backup Data</li>
              </ul>
            )}
          </div>

          {/* Appearance */}
          <div className="details-container">
            <button
              className="expand-button"
              onClick={() => setShowAppearance(!showAppearance)}
            >
              {showAppearance ? "Hide" : "Show"} Appearance
            </button>
            {showAppearance && (
              <ul className="user-details">
                <li>Theme: {darkMode ? "Dark" : "Light"}</li>
                <li>
                  <button className="action-button" onClick={toggleDarkMode}>
                    Toggle {darkMode ? "Light" : "Dark"} Mode
                  </button>
                </li>
              </ul>
            )}
          </div>
        </>
      )}
      <div className="actions">
        <button className="action-button" onClick={openLogoutModal}>
          Logout
        </button>
      </div>
      {showLogoutModal && (
        <div className="logout-modal">
          <div className="logout-modal-content">
            <p>Are you sure you want to logout?</p>
            <div className="modal-actions">
              <button
                className="modal-cancel-button"
                onClick={closeLogoutModal}
              >
                Cancel
              </button>
              <button className="modal-logout-button" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </div>
        </div>
      )}
      <ToastContainer />
    </div>
  );
}

export default UserProfile;
