import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils';

function Login() {
    const [loginInfo, setLoginInfo] = useState({
        emailOrUsername: '', // Adjusted the name to match backend field
        password: ''
    });

    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        console.log(name,value);
        const copyLoginInfo = { ...loginInfo };
        copyLoginInfo[name] = value;
        setLoginInfo(copyLoginInfo);
    };

    const handleLogin = async (e) => {
        e.preventDefault();
        const { emailOrUsername, password } = loginInfo;
        if (!emailOrUsername || !password) {
            return handleError('Email/Username and password are required');
        }
        try {
            const url = `http://localhost:8080/auth/login`;
            const response = await fetch(url, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(loginInfo)
            });
            const result = await response.json();
            const { success, message, jwtToken, user, error } = result;

            if (success) {
                handleSuccess(message);
                localStorage.setItem('token', jwtToken);
                localStorage.setItem('loggedInUser', JSON.stringify(user)); // Store the whole user object
                setTimeout(() => {
                    navigate('/home');
                }, 1000);
            } else if (error) {
                const details = error?.details[0].message;
                handleError(details);
            } else if (!success) {
                handleError(message);
            }
        } catch (err) {
            handleError('Something went wrong. Please try again later.');
        }
    };

    return (
        <div className='container'>
        <h1>Login</h1>
        <form onSubmit={handleLogin}>
            <div className='form-group'>
                <input
                    onChange={handleChange}
                    type='text'
                    name='emailOrUsername'
                    required
                    placeholder=' ' // Space to enable floating effect
                    value={loginInfo.emailOrUsername}
                    id='emailOrUsername'
                />
                <label className='form-label' htmlFor='emailOrUsername'>Email or Username</label>
            </div>
            <div className='form-group'>
                <input
                    onChange={handleChange}
                    type='password'
                    name='password'
                    required
                    placeholder=' ' // Space to enable floating effect
                    value={loginInfo.password}
                    id='password'
                />
                <label className='form-label' htmlFor='password'>Password</label>
            </div>
            <button type='submit'>Login</button>
            <span>Don't have an account? 
                <Link to="/signup">Signup</Link>
            </span>
        </form>
        <ToastContainer />
    </div>
    
    );
}

export default Login;
