import React from "react";
import "../../../assets/style/ServicesStyle/ChatServicesStyle/ChatList.css";

const ChatList = ({ selectedUser, handleUserSelect, multiSelect = false, selectedUsers = [] }) => {
  const users = [
    {
      id: 1,
      name: "John Doe",
      lastMessage: "Hey, how are you?",
      time: "2:30 PM",
      avatar: "/images/avatar/avatar-Alice.png",
      unread: 2,
      active: true,
      status: "seen",
    },
    {
      id: 2,
      name: "Jane Smith",
      lastMessage: "Can we meet tomorrow?",
      time: "Yesterday",
      avatar: "/images/avatar/avatar-Bob.png",
      unread: 0,
      active: false,
      status: "delivered",
    },
    {
      id: 3,
      name: "Bob Johnson",
      lastMessage: "Thanks for your help!",
      time: "2 days ago",
      avatar: "/images/avatar/avatar-Charlie.png",
      unread: 5,
      active: true,
      status: "sent",
    },xz
  ];

  return (
    <div className="chat-sidebar">
      <div className="chat-header-top-bar">
        <input type="text" className="user-search-bar" placeholder="Search" />
      </div>
      <ul className="chat-list">
        {users.map((user) => (
          <li
            key={user.id}
            className={`chat-list-item ${
              (multiSelect ? selectedUsers.some(u => u.id === user.id) : selectedUser?.id === user.id) ? "active" : ""
            }`}
            onClick={() => handleUserSelect(user)}
          >
            <div className="chat-item-content">
              <div className="avatar-container">
                <img
                  src={user.avatar}
                  alt={user.name}
                  className="chat-avatar"
                />
                {multiSelect && selectedUsers.some(u => u.id === user.id) && (
                  <div className="selected-overlay">âœ“</div>
                )}
              </div>
              <div className="chat-info">
                <div className="chat-header">
                  <span className={`chat-name ${user.unread > 0 ? "unread" : ""}`}>
                    {user.name}
                  </span>
                  {!multiSelect && <span className="chat-time">{user.time}</span>}
                </div>
                {!multiSelect && (
                  <div className="chat-footer">
                    <span className="chat-last-message">
                      {user.status && (
                        <span className={`message-status ${user.status}`}>
                          {/* ... (status icons remain unchanged) ... */}
                        </span>
                      )}
                      {user.lastMessage}
                    </span>
                    {user.unread > 0 && (
                      <span className="unread-badge">{user.unread}</span>
                    )}
                  </div>
                )}
              </div>
            </div>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ChatList;
