import React, { useState, useEffect } from 'react';
import '../../assets/style/Services/ChatService.css';

const ChatService = ({ onClose }) => {
  const [message, setMessage] = useState('');
  const [selectedUser, setSelectedUser] = useState(null);

  const handleSendMessage = () => {
    console.log('Sending message:', message);
    setMessage('');
  };

  const handleUserSelect = (user) => {
    setSelectedUser(user);
  };

  useEffect(() => {
    const chatContainer = document.querySelector('.chat-messages-container');
    if (chatContainer) {
      chatContainer.scrollTop = chatContainer.scrollHeight;
    }
  }, [messages, selectedUser]);

  return (
    <div className="chat-container-main-wrapper">
      <div className="chat-sidebar">
        <div className="chat-header-top-bar">
          <input type="text" className="user-search-bar" placeholder="Search..." />
        </div>
        <ul className="chat-list">
          {[
            { id: 1, name: "John Doe", lastMessage: "Hey, how are you?", time: "2:30 PM", avatar: "/images/avatar/user1.png" },
            { id: 2, name: "Jane Smith", lastMessage: "Can we meet tomorrow?", time: "Yesterday", avatar: "/images/avatar/user2.png" },
            { id: 3, name: "Bob Johnson", lastMessage: "Thanks for your help!", time: "2 days ago", avatar: "/images/avatar/user3.png" }
          ].map((user) => (
            <li key={user.id} className="chat-list-item" onClick={() => handleUserSelect(user)}>
              <img src={user.avatar} alt={user.name} className="chat-avatar" />
              <div className="chat-info">
                <span className="chat-name">{user.name}</span>
                <span className="chat-last-message">{user.lastMessage}</span>
              </div>
              <span className="chat-time">{user.time}</span>
            </li>
          ))}
        </ul>
      </div>

      <div className="chat-main">
        {selectedUser ? (
          <>
            <div className="chat-header">
              <div className="user-profile-section">
                <img src={selectedUser.avatar} alt={selectedUser.name} className="chat-avatar" />
                <div className="user-info">
                  <p className="user-name">{selectedUser.name}</p>
                </div>
              </div>
              <div className="call-buttons-container">
                <button className="circle-icon-btn">
                  <img src="/images/icon/video-call.png" alt="Video Call" className="call-icon-img" />
                </button>
                <button className="circle-icon-btn">
                  <img src="/images/icon/audio-call.png" alt="Audio Call" className="call-icon-img" />
                </button>
              </div>
            </div>

            <div className="chat-messages-container">
              {/* Messages will be displayed here */}
            </div>

            <div className="chat-input-section">
              <div className="input-wrapper">
                <button className="document-btn" onClick={() => console.log("Document button clicked")}>
                  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#5E5CE6" viewBox="0 0 16 16">
                    <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M8.5 4.5a.5.5 0 0 0-1 0v3h-3a.5.5 0 0 0 0 1h3v3a.5.5 0 0 0 1 0v-3h3a.5.5 0 0 0 0-1h-3z" />
                  </svg>
                </button>
                <textarea
                  className="message-input"
                  placeholder="Type a message..."
                  maxLength="5000"
                  value={message}
                  onChange={(e) => setMessage(e.target.value)}
                  onKeyPress={(e) => {
                    if (e.key === "Enter" && !e.shiftKey) {
                      e.preventDefault();
                      handleSendMessage();
                    }
                  }}
                />
                <button
                  className={`send-btn ${!message.trim() ? 'deactivated' : ''}`}
                  onClick={handleSendMessage}
                  disabled={!message.trim()}
                >
                  <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" viewBox="0 0 16 16" fill="#5E5CE6">
                    <path d="M16 8A8 8 0 1 0 0 8a8 8 0 0 0 16 0m-7.5 3.5a.5.5 0 0 1-1 0V5.707L5.354 7.854a.5.5 0 1 1-.708-.708l3-3a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1-.708 .708L8.5 5.707z"/>
                  </svg>
                </button>
              </div>
              <div className="char-count">
                {message.length}/5000
              </div>
            </div>
          </>
        ) : (
          <div className="no-chat-selected">
            <p>Select a chat to start messaging</p>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatService;
