import React, { useState, useEffect, useRef, useCallback } from "react";
import "../../../assets/style/ServicesStyle/ChatServicesStyle/ChatService.css";
import MessageInput from "./MessageInput";
import StarIcon from "../../StarIcon";

const ChatService = ({ onClose }) => {
  const [message, setMessage] = useState("");
  const [selectedUser, setSelectedUser] = useState(null);
  const [messages, setMessages] = useState({});
  const messagesEndRef = useRef(null);
  const [reactions, setReactions] = useState({});
  const [showReactions, setShowReactions] = useState({});

  const updateMessageStatus = useCallback(
    (messageObj, newStatus) => {
      if (selectedUser) {
        setMessages((prevMessages) => ({
          ...prevMessages,
          [selectedUser.id]: prevMessages[selectedUser.id].map((msg) =>
            msg === messageObj ? { ...msg, status: newStatus } : msg
          ),
        }));
      }
    },
    [selectedUser]
  );

  const handleSendMessage = useCallback(
    (newMessage) => {
      if (newMessage.trim() && selectedUser) {
        const newMessageObj = {
          text: newMessage,
          sender: "user",
          timestamp: new Date(),
          status: "sent",
          type: "text",
          id: Date.now(),
        };
        setMessages((prevMessages) => ({
          ...prevMessages,
          [selectedUser.id]: [
            ...(prevMessages[selectedUser.id] || []),
            newMessageObj,
          ],
        }));

        // Simulate message status changes
        setTimeout(() => updateMessageStatus(newMessageObj, "delivered"), 1000);
        setTimeout(() => updateMessageStatus(newMessageObj, "seen"), 2000);
      }
    },
    [selectedUser, updateMessageStatus]
  );

  const handleSendFile = useCallback(
    (file) => {
      if (file && selectedUser) {
        const newMessageObj = {
          file: file,
          sender: "user",
          timestamp: new Date(),
          status: "sent",
          type: file.type.startsWith("image/") ? "image" : "file",
          id: Date.now(),
        };
        setMessages((prevMessages) => ({
          ...prevMessages,
          [selectedUser.id]: [
            ...(prevMessages[selectedUser.id] || []),
            newMessageObj,
          ],
        }));

        // Simulate message status changes
        setTimeout(() => updateMessageStatus(newMessageObj, "delivered"), 1000);
        setTimeout(() => updateMessageStatus(newMessageObj, "seen"), 2000);
      }
    },
    [selectedUser, updateMessageStatus]
  );

  const handleUserSelect = useCallback((user) => {
    setSelectedUser(user);
    setMessages((prevMessages) => {
      if (!prevMessages[user.id]) {
        return {
          ...prevMessages,
          [user.id]: [
            {
              text: `Hey there! This is a demo message for ${user.name}.`,
              sender: "other",
              timestamp: new Date(Date.now() - 1000000),
              status: "seen",
              type: "text",
              id: Date.now(),
            },
            {
              text: "How can I help you today?",
              sender: "other",
              timestamp: new Date(Date.now() - 500000),
              status: "seen",
              type: "text",
              id: Date.now(),
            },
          ],
        };
      }
      return prevMessages;
    });
  }, []);

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  }, [messages, selectedUser]);

  const formatTime = useCallback((date) => {
    return date.toLocaleTimeString([], { hour: "2-digit", minute: "2-digit" });
  }, []);

  const handleReaction = useCallback((messageId, reaction) => {
    setReactions(prevReactions => ({
      ...prevReactions,
      [messageId]: reaction === prevReactions[messageId] ? null : reaction
    }));
    setShowReactions(prevShow => ({
      ...prevShow,
      [messageId]: false
    }));
  }, []);

  const toggleReactionMenu = useCallback((messageId) => {
    setShowReactions(prevShow => ({
      ...prevShow,
      [messageId]: !prevShow[messageId]
    }));
  }, []);

  const renderChatList = useCallback(
    () => (
      <ul className="chat-list">
        {[
          {
            id: 1,
            name: "John Doe",
            lastMessage: "Hey, how are you?",
            time: "2:30 PM",
            avatar: "/images/avatar/avatar-Alice.png",
            unread: 2,
            active: true,
            status: "seen",
          },
          {
            id: 2,
            name: "Jane Smith",
            lastMessage: "Can we meet tomorrow?",
            time: "Yesterday",
            avatar: "/images/avatar/avatar-Bob.png",
            unread: 0,
            active: false,
            status: "delivered",
          },
          {
            id: 3,
            name: "Bob Johnson",
            lastMessage: "Thanks for your help!",
            time: "2 days ago",
            avatar: "/images/avatar/avatar-Charlie.png",
            unread: 5,
            active: true,
            status: "sent",
          },
        ].map((user) => (
          <li
            key={user.id}
            className={`chat-list-item ${
              selectedUser?.id === user.id ? "active" : ""
            }`}
            onClick={() => handleUserSelect(user)}
          >
            <div className="chat-item-content">
              <div className="avatar-container">
                <img
                  src={user.avatar}
                  alt={user.name}
                  className="chat-avatar"
                />
              </div>
              <div className="chat-info">
                <div className="chat-header">
                  <span
                    className={`chat-name ${user.unread > 0 ? "unread" : ""}`}
                  >
                    {user.name}
                  </span>
                  <span className="chat-time">{user.time}</span>
                </div>
                <div className="chat-footer">
                  <span className="chat-last-message">
                    {/* Add message status icon */}
                    {user.status && (
                      <span className={`message-status ${user.status}`}>
                        {user.status === "seen" ? (
                          <img
                            src={user.avatar}
                            alt={`${user.name} avatar`}
                            className="seen-avatar"
                          />
                        ) : user.status === "delivered" ? (
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="16"
                            height="16"
                            fill="currentColor"
                            viewBox="0 0 16 16"
                          >
                            <path d="M12.354 4.354a.5.5 0 0 0-.708-.708L5 10.293 1.854 7.146a.5.5 0 1 0-.708.708l3.5 3.5a.5.5 0 0 0 .708 0l7-7zm-4.208 7-.896-.897.707-.707.543.543 6.646-6.647a.5.5 0 0 1 .708.708l-7 7a.5.5 0 0 1-.708 0z" />
                          </svg>
                        ) : (
                          <svg
                            xmlns="http://www.w3.org/2000/svg"
                            width="16"
                            height="16"
                            fill="currentColor"
                            viewBox="0 0 16 16"
                          >
                            <path d="M12.736 3.97a.733.733 0 0 1 1.047 0c.286.289.29.756.01 1.05L7.88 12.01a.733.733 0 0 1-1.065.02L3.217 8.384a.757.757 0 0 1 0-1.06.733.733 0 0 1 1.047 0l3.052 3.093 5.4-6.425a.247.247 0 0 1 .02-.022Z" />
                          </svg>
                        )}
                      </span>
                    )}

                    {user.lastMessage}
                  </span>
                  {user.unread > 0 && (
                    <span className="unread-badge">{user.unread}</span>
                  )}
                </div>
              </div>
            </div>
          </li>
        ))}
      </ul>
    ),
    [selectedUser, handleUserSelect]
  );

  const renderMessageGroups = useCallback(() => {
    if (!selectedUser || !messages[selectedUser.id]) {
      return null;
    }

    return messages[selectedUser.id]
      .reduce((groups, msg, index, array) => {
        const prevMsg = array[index - 1];
        const timeDiff = prevMsg ? msg.timestamp - prevMsg.timestamp : Infinity;
        const isNewGroup =
          !prevMsg || timeDiff > 30000 || prevMsg.sender !== msg.sender;

        if (isNewGroup) {
          groups.push([msg]);
        } else {
          groups[groups.length - 1].push(msg);
        }
        return groups;
      }, [])
      .map((group, groupIndex) => (
        <div
          key={groupIndex}
          className={`message-group ${
            group[0].sender === "user" ? "user-group" : "other-group"
          }`}
        >
          <div className="message-timestamp-center">
            {formatTime(group[0].timestamp)}
          </div>
          <div className="message-group-content">
            {group[0].sender !== "user" && (
              <img
                src={selectedUser.avatar}
                alt={selectedUser.name}
                className="message-avatar"
              />
            )}
            <div className="message-bubble-group">
              {group.map((msg, msgIndex) => (
                <div key={msgIndex} className="message-bubble">
                  {msg.type === "text" && (
                    <div className="message-content">{msg.text}</div>
                  )}
                  {msg.type === "image" && (
                    <img
                      src={URL.createObjectURL(msg.file)}
                      alt="Sent image"
                      className="message-image"
                    />
                  )}
                  {msg.type === "file" && (
                    <div className="message-file">
                      <a
                        href={URL.createObjectURL(msg.file)}
                        download={msg.file.name}
                      >
                        {msg.file.name}
                      </a>
                    </div>
                  )}
                  <div className="message-reaction-container">
                    {reactions[msg.id] ? (
                      <span 
                        className="reaction" 
                        onClick={() => toggleReactionMenu(msg.id)}
                      >
                        {reactions[msg.id]}
                      </span>
                    ) : (
                      <button
                        onClick={() => toggleReactionMenu(msg.id)}
                        className="add-reaction-btn"
                      >
                        😀
                      </button>
                    )}
                    {showReactions[msg.id] && (
                      <div className="reaction-menu">
                        <button onClick={() => handleReaction(msg.id, "like")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#like-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M12.162 7.338c.176.123.338.245.338.674 0 .43-.229.604-.474.725.1.163.132.36.089.546-.077.344-.392.611-.672.69.121.194.159.385.015.62-.185.295-.346.407-1.058.407H7.5c-.988 0-1.5-.546-1.5-1V7.665c0-1.23 1.467-2.275 1.467-3.13L7.361 3.47c-.005-.065.008-.224.058-.27.08-.079.301-.2.635-.2.218 0 .363.041.534.123.581.277.732.978.732 1.542 0 .271-.414 1.083-.47 1.364 0 0 .867-.192 1.879-.199 1.061-.006 1.749.19 1.749.842 0 .261-.219.523-.316.666ZM3.6 7h.8a.6.6 0 0 1 .6.6v3.8a.6.6 0 0 1-.6.6h-.8a.6.6 0 0 1-.6-.6V7.6a.6.6 0 0 1 .6-.6Z"/>
                            <defs>
                              <linearGradient id="like-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#18AFFF"/>
                                <stop offset="1" stop-color="#0062DF"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                        <button onClick={() => handleReaction(msg.id, "love")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#love-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41Z"/>
                            <defs>
                              <linearGradient id="love-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#FF6680"/>
                                <stop offset="1" stop-color="#E61739"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                        <button onClick={() => handleReaction(msg.id, "haha")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#haha-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41Z"/>
                            <defs>
                              <linearGradient id="haha-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#FFD700"/>
                                <stop offset="1" stop-color="#FF8C00"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                        <button onClick={() => handleReaction(msg.id, "wow")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#wow-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41Z"/>
                            <defs>
                              <linearGradient id="wow-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#FF6347"/>
                                <stop offset="1" stop-color="#FF4500"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                        <button onClick={() => handleReaction(msg.id, "sad")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#sad-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41Z"/>
                            <defs>
                              <linearGradient id="sad-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#4682B4"/>
                                <stop offset="1" stop-color="#191970"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                        <button onClick={() => handleReaction(msg.id, "care")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#care-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41Z"/>
                            <defs>
                              <linearGradient id="care-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#90EE90"/>
                                <stop offset="1" stop-color="#008000"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                        <button onClick={() => handleReaction(msg.id, "angry")} className="reaction-btn">
                          <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-svg">
                            <path fill="url(#angry-gradient)" d="M8 0a8 8 0 1 0 0 16A8 8 0 0 0 8 0Z"/>
                            <path fill="#fff" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41Z"/>
                            <defs>
                              <linearGradient id="angry-gradient" x1="8" x2="8" y2="16" gradientUnits="userSpaceOnUse">
                                <stop stop-color="#FF4500"/>
                                <stop offset="1" stop-color="#8B0000"/>
                              </linearGradient>
                            </defs>
                          </svg>
                        </button>
                      </div>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>

          {/* Message status displayed below the message bubble */}
          {group[group.length - 1].sender === "user" && (
            <div className="message-status">
              {group[group.length - 1].status === "sent" && <span>sent</span>}
              {group[group.length - 1].status === "delivered" && (
                <span>delivered</span>
              )}
              {group[group.length - 1].status === "seen" && (
                <div className="seen-status">
                  <img
                    src={selectedUser.avatar}
                    alt={selectedUser.name}
                    className="seen-avatar"
                  />
                  <span className="seen-text"></span>
                </div>
              )}
            </div>
          )}
        </div>
      ));
  }, [messages, selectedUser, formatTime, reactions, showReactions, handleReaction, toggleReactionMenu]);

  return (
    <div className="chat-container-main-wrapper">
      <div className="chat-sidebar">
        <div className="chat-header-top-bar">
          <input type="text" className="user-search-bar" placeholder="Search" />
        </div>
        {renderChatList()}
      </div>

      <div className="chat-main">
        {selectedUser ? (
          <>
            <div className="massage-box-chat-header">
              <div className="user-profile-section">
                <div className="avatar-container">
                  <img
                    src={selectedUser.avatar}
                    alt={selectedUser.name}
                    className="chat-avatar"
                  />
                </div>
                <div className="massage-box-top-bar-user-info">
                  <p className="massage-box-top-bar-user-name">
                    {selectedUser.name}
                  </p>
                  <div className="user-status">
                    {selectedUser.active && (
                      <p className="active-now ">Active now</p>
                    )}

                    {selectedUser.active && (
                      <div className="active-status"></div>
                    )}
                  </div>
                </div>
              </div>
              <div className="call-buttons-container">
                <button
                  className="icon-btn-video-call-btn"
                  aria-label="Video Call"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    fill="currentColor"
                    viewBox="0 0 48 48"
                  >
                    <path d="M32 32.5c0 3.59-2.91 6.5-6.5 6.5h-15C6.91 39 4 36.09 4 32.5v-17C4 11.91 6.91 9 10.5 9h15c3.59 0 6.5 2.91 6.5 6.5V32.5zM43.239 13.195c-.47-.266-1.047-.259-1.511.019L34 17.851v12.298l7.729 4.637C41.966 34.929 42.232 35 42.5 35c.255 0 .51-.065.739-.195C43.709 34.539 44 34.041 44 33.5v-19C44 13.959 43.709 13.461 43.239 13.195z" />
                  </svg>
                </button>
                <button
                  className="icon-btn-audio-call-btn"
                  aria-label="Audio Call"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    fill="currentColor"
                    viewBox="0 0 16 16"
                  >
                    <path
                      fillRule="evenodd"
                      d="M1.885.511a1.745 1.745 0 0 1 2.61.163L6.29 2.98c.329.423.445.974.315 1.494l-.547 2.19a.68.68 0 0 0 .178.643l2.457 2.457a.68.68 0 0 0 .644.178l2.189-.547a1.75 1.75 0 0 1 1.494.315l2.306 1.794c.829.645.905 1.87.163 2.611l-1.034 1.034c-.74.74-1.846 1.065-2.877.702a18.6 18.6 0 0 1-7.01-4.42 18.6 18.6 0 0 1-4.42-7.009c-.362-1.03-.037-2.137.703-2.877z"
                    />
                  </svg>
                </button>
                <button
                  className="icon-btn-user-info-btn"
                  aria-label="User info"
                >
                  <svg
                    xmlns="http://www.w3.org/2000/svg"
                    width="20"
                    height="20"
                    fill="currentColor"
                    viewBox="0 0 16 16"
                  >
                    <path d="M9.5 13a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm0-5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0zm0-5a1.5 1.5 0 1 1-3 0 1.5 1.5 0 0 1 3 0z" />
                  </svg>
                </button>
              </div>
            </div>

            <div className="chat-messages-container">
              {renderMessageGroups()}
              <div ref={messagesEndRef} />
            </div>

            <div className="chat-input-container">
              <MessageInput
                onSendMessage={handleSendMessage}
                onSendFile={handleSendFile}
              />
            </div>
          </>
        ) : (
          <div className="welcome-container">
            <StarIcon
              width={64}
              height={64}
              fill="#5E5CE6"
              className="welcome-icon"
            />
            <h1 className="welcome-title">Hello...</h1>
            <p className="welcome-message">
              Connect with friends and colleagues in a secure environment.
              Select a user from the list to start chatting!
            </p>
            <div className="encryption-notice">
              <svg
                xmlns="http://www.w3.org/2000/svg"
                width="16"
                height="16"
                fill="currentColor"
                viewBox="0 0 16 16"
              >
                <path d="M8 1a2 2 0 0 1 2 2v4H6V3a2 2 0 0 1 2-2zm3 6V3a3 3 0 0 0-6 0v4a2 2 0 0 0-2 2v5a2 2 0 0 0 2 2h6a2 2 0 0 0 2-2V9a2 2 0 0 0-2-2z" />
              </svg>
              <span>End-to-end encrypted</span>
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default ChatService;
