import React, { useEffect, useState } from "react";
import { handleError, handleSuccess } from "../utils/ReactToastify";
import { ToastContainer } from "react-toastify";
import '../assets/style/ReactToastifyCustom.css';
import "../assets/style/Profile.css";
import { useNavigate } from "react-router-dom";

function UserProfile({ onClose }) {
  const navigate = useNavigate();
  const [user, setUser] = useState({});
  const [showLogoutModal, setShowLogoutModal] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const [expandedSection, setExpandedSection] = useState(null);

  useEffect(() => {
    fetchUserProfile();
  }, []);

  const fetchUserProfile = async () => {
    try {
      const response = await fetch("http://localhost:8080/Users/me", {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      });
      const result = await response.json();
      if (response.ok) {
        setUser(result);
      } else {
        handleError(result.message || "Failed to fetch user data. Please try again.");
      }
    } catch (err) {
      handleError("Network error. Please check your connection and try again.");
    }
  };

  const handleLogout = async () => {
    try {
      const response = await fetch("http://localhost:8080/auth/logout", {
        method: "POST",
        headers: {
          "Content-Type": "application/json",
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        body: JSON.stringify({
          refreshToken: localStorage.getItem("refreshToken"),
        }),
      });
      if (response.ok) {
        localStorage.removeItem("token");
        localStorage.removeItem("refreshToken");
        localStorage.removeItem("loggedInUser");
        handleSuccess("Logged out successfully!");
        setTimeout(() => navigate("/login"), 500);
      } else {
        handleError("Failed to log out. Please try again.");
      }
    } catch (err) {
      handleError("Network error. Please check your connection and try again.");
    }
  };

  const toggleSection = (section) => {
    setExpandedSection(expandedSection === section ? null : section);
  };

  const renderSection = (title, items) => (
    <div className="details-container">
      <div className="section-header">
        <p>{title}</p>
      </div>
      <div className="section-buttons">
        {items.map((item, index) => (
          <button key={index} className="section-button">{item}</button>
        ))}
      </div>
    </div>
  );

  const fullName = `${user.first_name || ''} ${user.last_name || ''}`;

  return (
    <div className={`profile-container ${darkMode ? "dark-mode" : ""}`}>
      <button className="close-button" onClick={onClose}>Ã—</button>
      <div className="user-info">
        <div className="avatar">
          <img src='/images/avater/avater.png' className='profile-picture' alt='Profile' />
        </div>
        <h1 className="user-name">{fullName}</h1>
      </div>

      {renderSection("User Information", [
        `Name: ${fullName}`,
        `Email: ${user.email}`,
        `Username: ${user.username}`,
        `Joined: ${new Date(user.createdAt).toLocaleDateString()}`
      ])}

      {renderSection("Account Settings", [
        "Change Password",
        "Update Email",
        "Manage Two-Factor Authentication",
        "Change Security Questions",
        "Connected Accounts",
        "Account Activity"
      ])}

      {renderSection("Notifications", [
        "Email Notifications",
        "Push Notifications",
        "SMS Notifications"
      ])}

      {renderSection("Privacy", [
        "Manage Blocked Users",
        "Privacy Settings",
        "Data Sharing Preferences"
      ])}

      {renderSection("Data & Storage", [
        "Data Usage",
        "Storage Space",
        "Download Your Data"
      ])}

      {renderSection("Appearance", [
        <div key="theme">
          Theme:
          <button
            className={`theme-icon-action-button ${darkMode ? "rotate-right" : "rotate-left"}`}
            onClick={() => setDarkMode(!darkMode)}
          >
            <img
              src={darkMode ? "/images/icon/moon.ico" : "/images/icon/sun.ico"}
              alt="Theme Icon"
              className="theme-icon"
            />
          </button>
        </div>
      ])}

      <div className="actions">
        <button className="action-button" onClick={() => setShowLogoutModal(true)}>
          Logout
        </button>
      </div>

      {showLogoutModal && (
        <div className="logout-modal">
          <div className="logout-modal-content">
            <p>Are you sure you want to logout?</p>
            <div className="modal-actions">
              <button
                className="modal-cancel-button"
                onClick={() => setShowLogoutModal(false)}
              >
                Cancel
              </button>
              <button className="modal-logout-button" onClick={handleLogout}>
                Logout
              </button>
            </div>
          </div>
        </div>
      )}
      
      <ToastContainer />
    </div>
  );
}

export default UserProfile;
