import React, { useState, useRef, useEffect } from "react";
import "../assets/style/PromptInput.css";

const MAX_CHARS = 1000;

const PromptInput = () => {
  const [message, setMessage] = useState("");
  const [isFocused, setIsFocused] = useState(false);
  const [showOptions, setShowOptions] = useState(false);
  const [isGenerateMode, setIsGenerateMode] = useState(true);
  const textareaRef = useRef(null);
  const recognitionRef = useRef(null);

  useEffect(() => {
    // Initialize the Web Speech API
    const SpeechRecognition = window.SpeechRecognition || window.webkitSpeechRecognition;
    if (SpeechRecognition) {
      const recognition = new SpeechRecognition();
      recognition.continuous = true;
      recognition.interimResults = true;
      recognition.lang = "en-US";
      
      recognition.onresult = (event) => {
        let interimTranscript = "";
        for (let i = event.resultIndex; i < event.results.length; i++) {
          const transcript = event.results[i][0].transcript;
          if (event.results[i].isFinal) {
            setMessage((prevMessage) => (prevMessage + transcript).slice(0, MAX_CHARS));
          } else {
            interimTranscript += transcript;
          }
        }
      };

      recognition.onerror = (event) => {
        console.error("Speech recognition error:", event.error);
      };

      recognition.onend = () => {
        console.log("Speech recognition stopped. Restarting...");
        recognition.start(); // Always restart when recognition stops
      };

      recognition.start();
      recognitionRef.current = recognition;
    } else {
      console.error("Speech Recognition API not supported by this browser.");
    }

    return () => {
      if (recognitionRef.current) {
        recognitionRef.current.stop();
      }
    };
  }, []);

  const handleSendMessage = () => {
    console.log("Sending message:", message);
    setMessage("");
    setIsGenerateMode(true); // Switch to generate button after sending
  };

  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    if (inputValue.length <= MAX_CHARS) {
      setMessage(inputValue);
      if (inputValue.trim()) {
        setIsGenerateMode(false); // Activate send button when message has content
      } else {
        setIsGenerateMode(true); // Default to generate button when no content
      }
    }
  };

  const toggleOptions = () => {
    setShowOptions(!showOptions);
  };

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = "5px";
      const scrollHeight = textareaRef.current.scrollHeight;
      textareaRef.current.style.height = `${Math.min(scrollHeight, 200)}px`;
    }
  }, [message]);

  return (
    <div className={`prompt-container ${isFocused ? "focused" : ""}`}>
      <div className="input-wrapper">
        <div className="document-btn-wrapper">
          <button
            className="document-btn"
            id="add-document-btn"
            onClick={toggleOptions}
          >
            <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="#5E5CE6" viewBox="0 0 16 16">
              <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M8.5 4.5a.5.5 0 0 0-1 0v3h-3a.5.5 0 0 0 0 1h3v3a.5.5 0 0 0 1 0v-3h3a.5.5 0 0 0 0-1h-3z" />
            </svg>
          </button>
          {showOptions && (
            <div className="document-options">
              <button className="option-btn" title="Camera">
                <img src="/images/icon/camera-fill.svg" alt="Camera" />
              </button>
              <button className="option-btn" title="Photos">
                <img src="/images/icon/image-fill.svg" alt="Photos" />
              </button>
              <button className="option-btn" title="File">
                <img src="/images/icon/file-earmark-fill.svg" alt="File" />
              </button>
              <button className="option-btn" title="Voice Message" onClick={() => console.log("Starting voice message")}>
                <img src="/images/icon/mic-fill.svg" alt="Voice Message" />
              </button>
            </div>
          )}
        </div>

        <textarea
          ref={textareaRef}
          id="message-input"
          className="message-input"
          placeholder="Send a message..."
          value={message}
          onChange={handleInputChange}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          onKeyPress={(e) => {
            if (e.key === "Enter" && !e.shiftKey) {
              e.preventDefault();
              handleSendMessage();
            }
          }}
        />

        <button
          className="send-btn"
          id="send-btn"
          title={message.trim() ? "Send Message" : "Generate"}
          onClick={handleSendMessage}
          disabled={isGenerateMode && !message.trim()} // Disable button when no content
        >
          {isGenerateMode ? (
            <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="#5E5CE6" viewBox="0 0 16 16">
              <path d="M16 8A8 8 0 1 0 0 8a8 8 0 0 0 16 0m-7.5 3.5a.5.5 0 0 1-1 0V5.707L5.354 7.854a.5.5 0 1 1-.708-.708l3-3a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1-.708.708L8.5 5.707z" />
            </svg>
          ) : (
            <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="#5E5CE6" viewBox="0 0 16 16">
              <path d="M5 3a3 3 0 0 1 6 0v5a3 3 0 0 1-6 0z"/>
              <path d="M3.5 6.5A.5.5 0 0 1 4 7v1a4 4 0 0 0 8 0V7a.5.5 0 0 1 1 0v1a5 5 0 0 1-4.5 4.975V15h3a.5.5 0 0 1 0 1h-7a.5.5 0 0 1 0-1h3v-2.025A5 5 0 0 1 3 8V7a.5.5 0 0 1 .5-.5"/>
            </svg>
          )}
        </button>
      </div>
      <div className="char-count">
        {message.length}/{MAX_CHARS}
      </div>
    </div>
  );
};

export default PromptInput;
