import React, { useState } from 'react';
import '../../../assets/style/ServicesStyle/ChatServicesStyle/ForwardPopup.css';

const demoUsers = [
  { id: 1, name: 'John Doe', avatar: 'https://example.com/avatar1.jpg' },
  { id: 2, name: 'Jane Smith', avatar: 'https://example.com/avatar2.jpg' },
  { id: 3, name: 'Alice Johnson', avatar: 'https://example.com/avatar3.jpg' },
  { id: 4, name: 'Bob Williams', avatar: 'https://example.com/avatar4.jpg' },
  { id: 5, name: 'Eva Brown', avatar: 'https://example.com/avatar5.jpg' },
];

const ForwardPopup = ({ onConfirm, onCancel }) => {
  const [selectedUsers, setSelectedUsers] = useState([]);
  const [searchTerm, setSearchTerm] = useState('');

  const toggleUser = (userId) => {
    setSelectedUsers(prevSelected => 
      prevSelected.includes(userId)
        ? prevSelected.filter(id => id !== userId)
        : [...prevSelected, userId]
    );
  };

  const handleConfirm = () => {
    onConfirm(selectedUsers);
  };

  const filteredUsers = demoUsers.filter(user =>
    user.name.toLowerCase().includes(searchTerm.toLowerCase())
  );

  return (
    <div className="forward-popup-overlay">
      <div className="forward-popup">
        <h2>Forward Message</h2>
        <div className="user-search-bar">
          <input 
            type="text" 
            placeholder="Search" 
            value={searchTerm}
            onChange={(e) => setSearchTerm(e.target.value)}
          />
        </div>
        <ul className="chat-list">
          {filteredUsers.map((user) => (
            <li key={user.id} className="chat-list-item">
              <div className="chat-item-content">
                <div className="avatar-container">
                  <img src={user.avatar} alt={user.name} className="chat-avatar" />
                </div>
                <div className="chat-info">
                  <span className="chat-name">{user.name}</span>
                </div>
                <div className="forward-checkbox">
                  <input
                    type="checkbox"
                    id={`user-${user.id}`}
                    checked={selectedUsers.includes(user.id)}
                    onChange={() => toggleUser(user.id)}
                  />
                </div>
              </div>
            </li>
          ))}
        </ul>
        <div className="forward-buttons">
          <button className="cancel-button" onClick={onCancel}>Cancel</button>
          <button 
            className="confirm-button" 
            onClick={handleConfirm} 
            disabled={selectedUsers.length === 0}
          >
            Forward
          </button>
        </div>
      </div>
    </div>
  );
};

export default ForwardPopup;
