// pages/Signup.js
import React, { useEffect } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import useAuthStore from '../store/useAuthStore';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';

function Signup() {
    const { signup, loading, isAuthenticated } = useAuthStore();
    const navigate = useNavigate();

    useEffect(() => {
        if (isAuthenticated) {
            navigate('/home');
        }
    }, [isAuthenticated, navigate]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        const { name, email, password, confirmPassword } = event.target.elements;
        if (password.value !== confirmPassword.value) {
            handleError('Passwords do not match');
            return;
        }
        await signup(name.value, email.value, password.value);
        if (isAuthenticated) handleSuccess('Signup successful!');
    };

    return (
        <div className="signup-container container">
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Sign up</h1>
            <form onSubmit={handleSubmit}>
                <div className='form-group'>
                    <input type='text' name='name' placeholder='Name' required />
                </div>
                <div className='form-group'>
                    <input type='email' name='email' placeholder='Email' required />
                </div>
                <div className='form-group'>
                    <input type='password' name='password' placeholder='Password' required />
                </div>
                <div className='form-group'>
                    <input type='password' name='confirmPassword' placeholder='Confirm Password' required />
                </div>
                <button type='submit' disabled={loading}>Sign up</button>
                <span>Already have an account? <Link to="/login">Login</Link></span>
            </form>
            <ToastContainer />
        </div>
    );
}

export default Signup;
