import React from 'react';

function Appearance({ darkMode, toggleDarkMode, showAppearance, toggleAppearance }) {
  return (
    <div className="details-container">
      <button className="expand-button" onClick={toggleAppearance}>
        {showAppearance ? "Hide" : "Show"} Appearance
      </button>
      {showAppearance && (
        <ul className="user-details">
          <li>Theme: {darkMode ? "Dark" : "Light"}</li>
          <li>
            <button className="action-button" onClick={toggleDarkMode}>
              Toggle {darkMode ? "Light" : "Dark"} Mode
            </button>
          </li>
        </ul>
      )}
    </div>
  );
}

export default Appearance;
