import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/style/profile.css';

function UserProfile() {
    const [user, setUser] = useState({});
    const [loading, setLoading] = useState(true);
    const [darkMode, setDarkMode] = useState(false);
    const [activeTab, setActiveTab] = useState('profile');
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserProfile();
    }, []);

    const fetchUserProfile = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:8080/Users/me', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            const result = await response.json();
            if (response.ok) {
                setUser(result);
            } else {
                handleError(result.message || 'Failed to fetch user data.');
                navigate('/login');
            }
        } catch (err) {
            handleError('Network error. Please check your connection.');
            navigate('/login');
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        try {
            const response = await fetch("http://localhost:8080/auth/logout", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ refreshToken: localStorage.getItem('refreshToken') })
            });
            if (response.ok) {
                localStorage.removeItem('token');
                localStorage.removeItem('refreshToken');
                handleSuccess('Logged out successfully!');
                setTimeout(() => navigate('/login'), 500);
            } else {
                handleError('Failed to log out.');
            }
        } catch (err) {
            handleError('Network error.');
        }
    };

    const toggleDarkMode = () => {
        setDarkMode(!darkMode);
    };

    return (
        <div className={`profile-container ${darkMode ? 'dark-mode' : ''}`}>
            <header className="profile-header">
                <button className="back-button" onClick={() => navigate(-1)}>Back</button>
                <div className="profile-picture-container">
                    <img src='/avater.png' alt='Profile' className='profile-picture' />
                </div>
                <button className="logout-button" onClick={handleLogout}>Logout</button>
            </header>
            
            <div className="profile-content">
                <nav className="profile-nav">
                    <button className={`nav-button ${activeTab === 'profile' ? 'active' : ''}`} onClick={() => setActiveTab('profile')}>Profile Information</button>
                    <button className={`nav-button ${activeTab === 'settings' ? 'active' : ''}`} onClick={() => setActiveTab('settings')}>Account Settings</button>
                    <button className={`nav-button ${activeTab === 'notifications' ? 'active' : ''}`} onClick={() => setActiveTab('notifications')}>Notifications</button>
                    <button className={`nav-button ${activeTab === 'privacy' ? 'active' : ''}`} onClick={() => setActiveTab('privacy')}>Privacy</button>
                    <button className={`nav-button ${activeTab === 'data' ? 'active' : ''}`} onClick={() => setActiveTab('data')}>Data & Storage</button>
                    <button className={`nav-button ${activeTab === 'appearance' ? 'active' : ''}`} onClick={() => setActiveTab('appearance')}>Appearance</button>
                </nav>

                <div className="tab-content">
                    {loading ? (
                        <div className="loading-overlay">
                            <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                        </div>
                    ) : (
                        <>
                            {activeTab === 'profile' && (
                                <div className="profile-info">
                                    <h2>User Information</h2>
                                    <ul>
                                        <li><strong>Name:</strong> {user.name}</li>
                                        <li><strong>Email:</strong> {user.email}</li>
                                        <li><strong>Username:</strong> {user.username}</li>
                                        <li><strong>Joined:</strong> {new Date(user.createdAt).toLocaleDateString()}</li>
                                    </ul>
                                </div>
                            )}
                            {activeTab === 'settings' && (
                                <div className="account-settings">
                                    <h2>Account Settings</h2>
                                    <button>Change Password</button>
                                    <button>Update Email</button>
                                    <button>Manage Two-Factor Authentication</button>
                                </div>
                            )}
                            {activeTab === 'notifications' && (
                                <div className="notifications">
                                    <h2>Notifications</h2>
                                    <button>Email Notifications</button>
                                    <button>Push Notifications</button>
                                    <button>SMS Notifications</button>
                                </div>
                            )}
                            {activeTab === 'privacy' && (
                                <div className="privacy">
                                    <h2>Privacy</h2>
                                    <button>Manage Blocked Users</button>
                                    <button>Privacy Settings</button>
                                    <button>Data Sharing Preferences</button>
                                </div>
                            )}
                            {activeTab === 'data' && (
                                <div className="data-storage">
                                    <h2>Data & Storage</h2>
                                    <button>Data Usage</button>
                                    <button>Storage Space</button>
                                    <button>Download Your Data</button>
                                </div>
                            )}
                            {activeTab === 'appearance' && (
                                <div className="appearance">
                                    <h2>Appearance</h2>
                                    <p>Theme: {darkMode ? 'Dark' : 'Light'}</p>
                                    <button onClick={toggleDarkMode}>
                                        Toggle {darkMode ? 'Light' : 'Dark'} Mode
                                    </button>
                                </div>
                            )}
                        </>
                    )}
                </div>
            </div>
            <ToastContainer />
        </div>
    );
}

export default UserProfile;
