// FindUserForgotPassword.js

import React, { useEffect, useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../assets/style/styleutils/ReactToastifyCustom.css';
import '../../assets/style/styleutils/animations.css';
import '../../assets/style/Authentication/FindUserForgotPassword.css';

function FindUserForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(true);
    const [showContent, setShowContent] = useState(false);
    const [suggestions, setSuggestions] = useState([]);
    const [showSuggestions, setShowSuggestions] = useState(false);
    const [searchCompleted, setSearchCompleted] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (fadeIn) {
            const timeoutId = setTimeout(() => setFadeIn(false), 500);
            return () => clearTimeout(timeoutId);
        }
    }, [fadeIn]);

    useEffect(() => {
        const timerId = setTimeout(() => setShowContent(!loading), 500);
        return () => clearTimeout(timerId);
    }, [loading]);

    const handleSearch = async (event) => {
        event.preventDefault();
        setLoading(true);
        setShowContent(false);

        try {
            const response = await fetch('http://localhost:8080/verification/find-user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ emailOrUsername }),
            });

            const result = await response.json();

            setTimeout(() => {
                if (response.ok) {
                    handleSuccess(result.message);
                    setUser(result.user);
                } else {
                    handleError(result.message);
                    setUser(null);
                }
                setFadeIn(true);
                setLoading(false);
                setSearchCompleted(true);
            }, 500);
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
            setFadeIn(true);
            setLoading(false);
            setSearchCompleted(true);
        }
    };

    const handleProceed = () => {
        if (user && searchCompleted) {
            // handleSendOtp();
            navigate('/forgot-password/verification-code', { state: { email: user.email } });
        } else {
            handleError('No user found. Please search for a valid user.');
        }
    };

    const handleInputChange = (event) => {
        const value = event.target.value;
        setEmailOrUsername(value);
        updateSuggestions(value);
    };

    const updateSuggestions = (value) => {
        const domains = ['gmail.com', 'yahoo.com', 'outlook.com', 'hotmail.com'];
        let suggestionList = [];

        if (value.includes('@')) {
            const [, domain] = value.split('@');
            if (domain.length > 0) {
                suggestionList = domains
                    .filter(d => d.startsWith(domain))
                    .map(d => `${value.split('@')[0]}@${d}`);
            }
        } else {
            suggestionList = domains.map(d => `${value}@${d}`);
        }

        setSuggestions(suggestionList);
        setShowSuggestions(suggestionList.length > 0);
    };

    const handleSuggestionClick = (suggestion) => {
        setEmailOrUsername(suggestion);
        setShowSuggestions(false);
    };

    return (
        <div className={`find-user-container container ${fadeIn ? 'fade-in' : ''}`}>
            <div className="header-container">
                <div className="icon-container-position">
                    <Link to="/login" className='icon-container'>
                        <svg xmlns="http://www.w3.org/2000/svg" width="24" height="24" fill="#800080" className="bi bi-arrow-left back-icon" viewBox="0 0 16 16">
                            <path fillRule="evenodd" d="M15 8a.5.5 0 0 0-.5-.5H2.707l3.147-3.146a.5.5 0 1 0-.708-.708l-4 4a.5.5 0 0 0 0 .708l4 4a.5.5 0 0 0 .708-.708L2.707 8.5H14.5A.5.5 0 0 0 15 8z"/>
                        </svg>
                    </Link>
                </div>

                <div className="center-content">
                    <div className="logo-container">
                        <svg xmlns="http://www.w3.org/2000/svg" width="80" height="80" fill="#800080" className="bi bi-stars app-icon" viewBox="0 0 16 16">
                            <path d="M7.657 6.247c.11-.33.576-.33.686 0l.645 1.937a2.89 2.89 0 0 0 1.829 1.828l1.936.645c.33.11.33.576 0 .686l-1.937.645a2.89 2.89 0 0 0-1.828 1.829l-.645 1.936a.361.361 0 0 1-.686 0l-.645-1.937a2.89 2.89 0 0 0-1.828-1.828l-1.937-.645a.361.361 0 0 1 0-.686l1.937-.645a2.89 2.89 0 0 0 1.828-1.828zM3.794 1.148a.217.217 0 0 1 .412 0l.387 1.162c.173.518.579.924 1.097 1.097l1.162.387a.217.217 0 0 1 0 .412l-1.162.387A1.73 1.73 0 0 0 4.593 5.69l-.387 1.162a.217.217 0 0 1-.412 0L3.407 5.69A1.73 1.73 0 0 0 2.31 4.593l-1.162-.387a.217.217 0 0 1 0-.412l1.162-.387A1.73 1.73 0 0 0 3.407 2.31zM10.863.099a.145.145 0 0 1 .274 0l.258.774c.115.346.386.617.732.732l.774.258a.145.145 0 0 1 0 .274l-.774.258a1.16 1.16 0 0 0-.732.732l-.258.774a.145.145 0 0 1-.274 0l-.258-.774a1.16 1.16 0 0 0-.732-.732L9.1 2.137a.145.145 0 0 1 0-.274l.774-.258c.346-.115.617-.386.732-.732z"/>
                        </svg>
                    </div>
                    <h1 className="page-title" style={{color: '#800080'}}>Forgot Password</h1>
                </div>
            </div>

            <form onSubmit={handleSearch}>
                <div className="input-container">
                    <input
                        type="search"
                        placeholder="Email or Username"
                        value={emailOrUsername}
                        onChange={handleInputChange}
                        required
                    />
                    <button type="submit" disabled={loading} className="search-icon-wrapper">
                        <img src="/images/icon/search.ico" alt="Search icon" className="search-icon-img" />
                    </button>
                    {showSuggestions && (
                        <ul className="suggestion-list">
                            {suggestions.map((suggestion, index) => (
                                <li key={index} onClick={() => handleSuggestionClick(suggestion)}>
                                    {suggestion}
                                </li>
                            ))}
                        </ul>
                    )}
                </div>
            </form>

            <div className="confirmation-message">
                {!searchCompleted ? (
                    <br />
                ) : (
                    <>
                        {user ? (
                            <p>Is this you? Please confirm.</p>
                        ) : (
                            <p>Please try again with a different email or username.</p>
                        )}
                    </>
                )}
            </div>

            <div className="found-user-wrapper">
                {loading && !showContent ? (
                    <div className="loading-overlay">
                        <img src='/images/icon/apple-loading.gif' alt='Loading spinner' className='loading-spinner' />
                    </div>
                ) : (
                    <div className={`found-user-content ${user && searchCompleted ? 'fade-in-bottom' : ''}`}>
                        {user ? (
                            <div
                                className="user-card"
                                onClick={handleProceed}
                                style={{ cursor: 'pointer', borderColor: '#800080' }}
                            >
                                <div className="user-card-image">
                                    <img src='/images/avater/avater.png' alt='User avatar' className='profilePicture' />
                                </div>
                                <div className="user-card-details">
                                    <h3>{user.name}</h3>
                                    <p>{user.username}</p>
                                </div>
                            </div>
                        ) : (
                            <div className="placeholder-message">
                                <p style={{color: '#800080'}}>Find your profile using Gmail and username</p>
                            </div>
                        )}
                    </div>

                )}
            </div>

            <ToastContainer />
        </div>
    );
}

export default FindUserForgotPassword;
