import React, { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { handleSuccess, handleError, ToastContainer } from "../../utils/ReactToastify";
import "../../assets/style/styleutils/ReactToastifyCustom.css";
import "../../assets/style/VerificationStyle/ResetPassword.css";
import PasswordStrengthMeter from "../../Extension/PasswordStrengthMeter"; 
import StarIcon from '../../components/StarIcon';
import { useForgotPasswordState } from '../../forgotPasswordState';

function ResetPassword() {
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [passwordError, setPasswordError] = useState(false);
  const [confirmPasswordError, setConfirmPasswordError] = useState(false);
  const [matchError, setMatchError] = useState(false);
  const { state, dispatch } = useForgotPasswordState();
  const navigate = useNavigate();

  useEffect(() => {
    if (!state.codeVerified) {
      navigate('/find-user');
    }
  }, [state.codeVerified, navigate]);

  const handleSubmit = async (event) => {
    event.preventDefault();
    let hasError = false;

    // Validate inputs
    if (!password) {
      setPasswordError(true);
      hasError = true;
    } else {
      setPasswordError(false);
    }

    if (!confirmPassword) {
      setConfirmPasswordError(true);
      hasError = true;
    } else {
      setConfirmPasswordError(false);
    }

    if (password !== confirmPassword) {
      setMatchError(true);
      handleError("Passwords do not match");
      hasError = true;
    } else {
      setMatchError(false);
    }

    if (hasError) return;

    try {
      const response = await fetch(
        "http://localhost:8080/verification/reset-password",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            "Authorization": `Bearer ${state.resetToken}`
          },
          body: JSON.stringify({ email: state.email, newPassword: password }),
        }
      );

      const result = await response.json();

      if (response.ok) {
        handleSuccess(result.message);
        dispatch({ type: 'RESET_STATE' });
        navigate("/login");
      } else {
        handleError(result.message);
      }
    } catch (err) {
      handleError("Network error. Please check your connection and try again.");
    }
  };

  return (
    <div className="reset-password-container fade-in-bottom">
      <StarIcon width={80} height={80}/>
      <h1 className="fade-in">Reset Password</h1>
      <form onSubmit={handleSubmit}>
        <div className={`input-wrapper ${matchError ? "error" : ""} fade-in`}>
          <div className={`form-group-reset-password ${passwordError ? "error" : ""}`}>
            <input
              type="password"
              id="new-password"
              placeholder=""
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <label htmlFor="new-password" className="form-label-reset-password">
              New password
            </label>
          </div>
          <div className={`form-group-reset-password ${confirmPasswordError ? "error" : ""}`}>
            <input
              type="password"
              id="confirm-password"
              placeholder=""
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            <label htmlFor="confirm-password" className="form-label-reset-password">
              Confirm Password
            </label>
          </div>
          <PasswordStrengthMeter password={password} />
        </div>
        <button type="submit" className="scale-in">
          Reset Password
        </button>
      </form>
      <ToastContainer />
    </div>
  );
}

export default ResetPassword;
