import React from "react";
import "../../../assets/style/ServicesStyle/ChatServicesStyle/MessageReactions.css";

const LikeIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16" className="reaction-icon">
    <path fill="#2078F4" d="M16 8A8 8 0 1 1-.001 8 8 8 0 0 1 16 8" />
    <path fill="#FFFFFF" d="M12.162 7.338c.176.123.338.245.338.674 0 .43-.229.604-.474.725.1.163.132.36.089.546-.077.344-.392.611-.672.69.121.194.159.385.015.62-.185.295-.346.407-1.058.407H7.5c-.988 0-1.5-.546-1.5-1V7.665c0-1.23 1.467-2.275 1.467-3.13L7.361 3.47c-.005-.065.008-.224.058-.27.08-.079.301-.2.635-.2.218 0 .363.041.534.123.581.277.732.978.732 1.542 0 .271-.414 1.083-.47 1.364 0 0 .867-.192 1.879-.199 1.061-.006 1.749.19 1.749.842 0 .261-.219.523-.316.666zM3.6 7h.8a.6.6 0 0 1 .6.6v3.8a.6.6 0 0 1-.6.6h-.8a.6.6 0 0 1-.6-.6V7.6a.6.6 0 0 1 .6-.6z" />
  </svg>
);

const LoveIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 16 16" className="reaction-icon">
    <path fill="#F33E58" d="M16 8A8 8 0 1 1-.001 8 8 8 0 0 1 16 8" />
    <path fill="#FFFFFF" d="M10.473 4C8.275 4 8 5.824 8 5.824S7.726 4 5.528 4c-2.114 0-2.73 2.222-2.472 3.41C3.736 10.55 8 12.75 8 12.75s4.265-2.2 4.945-5.34c.257-1.188-.36-3.41-2.472-3.41" />
  </svg>
);

const HahaIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-icon">
    <path fill="url(#haha-a)" d="M16 8A8 8 0 1 1-.001 8 8 8 0 0 1 16 8" />
    <path fill="url(#haha-b)" d="M3 8.008C3 10.023 4.006 14 8 14c3.993 0 5-3.977 5-5.992C13 7.849 11.39 7 8 7c-3.39 0-5 .849-5 1.008Z" />
    <path fill="url(#haha-c)" d="M4.541 12.5c.804.995 1.907 1.5 3.469 1.5 1.563 0 2.655-.505 3.459-1.5-.551-.588-1.599-1.5-3.459-1.5s-2.917.912-3.469 1.5Z" />
    <path fill="#2A3755" d="M6.213 4.144c.263.188.502.455.41.788-.071.254-.194.369-.422.37-.78.012-1.708.256-2.506.613-.065.029-.197.088-.332.085-.124-.003-.251-.058-.327-.237-.067-.157-.073-.388.276-.598.545-.33 1.257-.48 1.909-.604-.41-.303-.85-.56-1.315-.768-.427-.194-.38-.457-.323-.6.127-.317.609-.196 1.078.026a9 9 0 0 1 1.552.925Zm3.577 0a8.955 8.955 0 0 1 1.55-.925c.47-.222.95-.343 1.078-.026.057.143.104.406-.323.6a7.028 7.028 0 0 0-1.313.768c.65.123 1.363.274 1.907.604.349.21.342.44.276.598-.077.18-.203.234-.327.237-.135.003-.267-.056-.332-.085-.797-.357-1.725-.6-2.504-.612-.228-.002-.351-.117-.422-.37-.091-.333.147-.6.41-.788v-.001Z" />
    <defs>
      <linearGradient id="haha-a" x1="8" x2="8" y1="1.64" y2="16" gradientUnits="userSpaceOnUse">
        <stop stopColor="#FEEA70" />
        <stop offset="1" stopColor="#F69B30" />
      </linearGradient>
      <linearGradient id="haha-b" x1="8" x2="8" y1="7" y2="14" gradientUnits="userSpaceOnUse">
        <stop stopColor="#472315" />
        <stop offset="1" stopColor="#8B3A0E" />
      </linearGradient>
      <linearGradient id="haha-c" x1="8.005" x2="8.005" y1="11" y2="13.457" gradientUnits="userSpaceOnUse">
        <stop stopColor="#FC607C" />
        <stop offset="1" stopColor="#D91F3A" />
      </linearGradient>
    </defs>
  </svg>
);

const WowIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-icon">
    {/* WowIcon SVG content */}
  </svg>
);

const SadIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-icon">
    {/* SadIcon SVG content */}
  </svg>
);

const AngryIcon = () => (
  <svg xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 16 16" className="reaction-icon">
    {/* AngryIcon SVG content */}
  </svg>
);

const MessageReactions = ({
  messageId,
  reaction,
  showReactions,
  onReaction,
  onToggleReactionMenu,
}) => {
  const getReactionIcon = (reactionType) => {
    switch (reactionType) {
      case "like":
        return <LikeIcon />;
      case "love":
        return <LoveIcon />;
      case "haha":
        return <HahaIcon />;
      case "wow":
        return <WowIcon />;
      case "sad":
        return <SadIcon />;
      case "angry":
        return <AngryIcon />;
      default:
        return null;
    }
  };

  return (
    <div className="message-reaction-container">
      {reaction ? (
        <span
          className="reaction"
          onClick={() => onToggleReactionMenu(messageId)}
        >
          {getReactionIcon(reaction)}
        </span>
      ) : (
        <button
          onClick={() => onToggleReactionMenu(messageId)}
          className="add-reaction-btn"
        >
          <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="reaction-icon">
            <circle cx="12" cy="12" r="10" />
            <path d="M8 14s1.5 2 4 2 4-2 4-2" />
            <line x1="9" y1="9" x2="9.01" y2="9" />
            <line x1="15" y1="9" x2="15.01" y2="9" />
          </svg>
        </button>
      )}
      {showReactions && (
        <div className="reaction-menu">
          <button onClick={() => onReaction(messageId, "like")} className="reaction-btn">
            <LikeIcon />
          </button>
          <button onClick={() => onReaction(messageId, "love")} className="reaction-btn">
            <LoveIcon />
          </button>
          <button onClick={() => onReaction(messageId, "laughing")} className="reaction-btn">
            <LaughingIcon />
          </button>
          <button onClick={() => onReaction(messageId, "wow")} className="reaction-btn">
            <WowIcon />
          </button>
          <button onClick={() => onReaction(messageId, "sad")} className="reaction-btn">
            <SadIcon />
          </button>
          <button onClick={() => onReaction(messageId, "angry")} className="reaction-btn">
            <AngryIcon />
          </button>
        </div>
      )}
    </div>
  );
};

export default MessageReactions;
