import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function FindUserForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSearch = async (event) => {
        event.preventDefault();
        setLoading(true);
        try {
            const response = await fetch('http://localhost:8080/auth/forgot-password/find-user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ emailOrUsername }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                setUser(result.user);
            } else {
                handleError(result.message);
                setUser(null);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleProceed = () => {
        if (user) {
            navigate('/forgot-password/send-otp', { state: { email: user.email } });
        }
    };

    return (
        <div className="find-user-container container">
            <h1>Forgot Password</h1>
            <form onSubmit={handleSearch}>
                <input
                    type="text"
                    placeholder="Enter your email or username..."
                    value={emailOrUsername}
                    onChange={(e) => setEmailOrUsername(e.target.value)}
                    required
                />
                <button type="submit" disabled={loading}>
                    {loading ? 'Searching...' : 'Search'}
                </button>
            </form>
            <div className="found-user-container">
                {user ? (
                    <div
                        className="user-info-to"
                        onClick={handleProceed} // Added onClick event
                        style={{ cursor: 'pointer' }} // Added pointer cursor
                    >
                        <img src={user.avatar || '/avatar.png'} alt={user.name} />
                        <h3>{user.name}</h3>
                        <p>Username: {user.username}</p>
                    </div>
                ) : (
                    <p>No user found. Please try again.</p>
                )}
            </div>
            <ToastContainer />
        </div>
    );
}

export default FindUserForgotPassword;
