// pages/FindUserForgotPassword.js
import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function FindUserForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);

        try {
            const response = await fetch('http://localhost:8080/auth/find-user-forgot-password', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ emailOrUsername }),
            });

            const result = await response.json();
            if (response.ok) {
                navigate('/send-otp-forgot-password', { state: { email: result.user.email } });
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="findforgot-password container">
            <h1>Find Your Account</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type='text'
                    placeholder='Enter your email or username'
                    value={emailOrUsername}
                    onChange={(e) => setEmailOrUsername(e.target.value)}
                    required
                />
                <button type='submit'>Find Account</button>
            </form>
            <ToastContainer />
            {loading && <div>Loading...</div>}
        </div>
    );
}

export default FindUserForgotPassword;
