import React, { useCallback, useEffect, useState, lazy, Suspense } from "react";
import { useNavigate } from "react-router-dom";
import { handleError } from "../utils/ReactToastify";
import { ToastContainer } from "react-toastify";
import "../assets/style/ReactToastifyCustom.css";
import "../assets/style/home.css";
import "../assets/style/loading.css";
import LoadingOverlay from "../components/LoadingOverlay";
import ConfirmAccountPopup from "../components/ConfirmAccountPopup";

const LazyUserProfile = lazy(() => import('./UserProfile.jsx'));

function Home() {
  const [loggedInUser, setLoggedInUser] = useState({});
  const [loading, setLoading] = useState(true);
  const [fadeIn, setFadeIn] = useState(true);
  const [showProfile, setShowProfile] = useState(false);
  const [showConfirmPopup, setShowConfirmPopup] = useState(false);
  const navigate = useNavigate();

  const fetchLoggedInUser = useCallback(async () => {
    setLoading(true);
    try {
      const url = "http://localhost:8080/Users/me";
      const headers = {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
      };
      const response = await fetch(url, headers);
      const result = await response.json();
      if (response.ok) {
        setLoggedInUser(result);
      } else {
        handleError(
          result.message || "Failed to fetch user data. Please try again."
        );
        navigate("/login");
      }
    } catch (err) {
      handleError("Network error. Please check your connection and try again.");
      navigate("/login");
    } finally {
      setLoading(false);
    }
  }, [navigate]);

  useEffect(() => {
    fetchLoggedInUser();

    const timeoutId = setTimeout(() => {
      setFadeIn(false);
    }, 500);

    let popupInterval;

    if (!loggedInUser.isActive) {
      popupInterval = setInterval(() => {
        setShowConfirmPopup(true);
        setTimeout(() => setShowConfirmPopup(false), 3000); // Hide after 3 seconds
      }, 5000);
    }

    return () => {
      clearTimeout(timeoutId);
      if (popupInterval) {
        clearInterval(popupInterval);
      }
    };
  }, [fetchLoggedInUser, loggedInUser.isActive]);

  if (loading) {
    return <LoadingOverlay loading={loading} fadeOut={false} />;
  }

  return (
    <div className="home-container">
      <div className="background-overlay"></div>
      <div className="content-wrapper">
        <div className="top-bar">
          <button className="history-button">
            <img src="/images/icon/history-icon.png" alt="History" />
          </button>
          <div
            className="user-icon-image"
            onClick={() => setShowProfile(!showProfile)}
          >
            <img
              src="/images/avater/avater.png"
              className="profilePicture"
              alt="Profile"
            />
          </div>
        </div>
        <div className="main-home-container">
          <h1 className="user-name">{loggedInUser.name}</h1>
          {showProfile && (
            <div className="centered-profile">
              <Suspense fallback={<div>Loading profile...</div>}>
                <LazyUserProfile
                  user={loggedInUser}
                  onClose={() => setShowProfile(false)}
                />
              </Suspense>
            </div>
          )}
          <ConfirmAccountPopup
            isActive={loggedInUser.isActive}
            email={loggedInUser.email}
            token={localStorage.getItem("token")}
            show={showConfirmPopup}
          />
        </div>


        <div className="top-services-main-container">
          <div className="top-services-container">
            <label className="services-level">Chat</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>

        </div>


        <div className="top-services-main-container">
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>

        </div>

        <div className="top-services-main-container">
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">AI</label>
          </div>
          <div className="top-services-container">
            <label className="services-level">MORE</label>
            <h5>1000+</h5>
          </div>
          
        </div>




    <div className="main-input-container">
         <dir>


         </dir>
    </div>







      </div>
      <ToastContainer />
    </div>
  );
}

export default Home;
