import React, { useState, useEffect, useRef } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../utils/ReactToastifyCustom.css';
import '../../utils/style/SentOtpForgotPassword.css';

const OtpInput = ({ length = 6, onOtpSubmit = () => { }, onOtpComplete = () => { } }) => {
    const [otp, setOtp] = useState(new Array(length).fill(""));
    const [isComplete, setIsComplete] = useState(false);
    const inputRefs = useRef([]);

    useEffect(() => {
        if (inputRefs.current[0]) {
            inputRefs.current[0].focus();
        }
    }, []);

    useEffect(() => {
        onOtpComplete(isComplete);
    }, [isComplete, onOtpComplete]);

    const handleChange = (index, e) => {
        const value = e.target.value;
        if (isNaN(value) || value.length > 1) return;

        const newOtp = [...otp];
        newOtp[index] = value.slice(-1);
        setOtp(newOtp);

        const combinedOtp = newOtp.join("");
        const isComplete = combinedOtp.length === length;
        setIsComplete(isComplete);

        if (value && index < length - 1) {
            inputRefs.current[index + 1].focus();
        }
    };

    const handleClick = (index) => {
        inputRefs.current[index].setSelectionRange(1, 1);
        inputRefs.current[index].select();
    };

    const handleKeyDown = (index, e) => {
        if (e.key === "Backspace") {
            if (!otp[index] && index > 0) {
                inputRefs.current[index - 1].focus();
            }
        } else if (e.key === "Enter") {
            if (index < length - 1) {
                inputRefs.current[index + 1].focus();
            } else if (index === length - 1) {
                if (isComplete) {
                    onOtpSubmit(otp.join(""));
                }
            }
        }
    };

    const handlePaste = (e) => {
        e.preventDefault();
        const pasteData = e.clipboardData.getData('text').slice(0, length);
        setOtp(pasteData.split(''));
        inputRefs.current[length - 1].focus();
        onOtpSubmit(pasteData);
        onOtpComplete(true);
    };

    return (
        <div className="otp-input-wrapper">
            {otp.map((value, index) => (
                <input
                    key={index}
                    type="text"
                    ref={(input) => (inputRefs.current[index] = input)}
                    value={value}
                    onChange={(e) => handleChange(index, e)}
                    onKeyDown={(e) => handleKeyDown(index, e)}
                    onPaste={(e) => handlePaste(e)}
                    className="otpField"
                    maxLength={1}
                    onClick={() => handleClick(index)}
                />
            ))}
        </div>
    );
};

function VerifyCodeConfirmAccount() {
    const [otp, setOtp] = useState('');
    const [timer, setTimer] = useState(60);
    const [isResendEnabled, setIsResendEnabled] = useState(false);
    const [isComplete, setIsComplete] = useState(false);
    const [isCodeSent, setIsCodeSent] = useState(false); // Track if code is sent
    const { state } = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        // Load timer from localStorage
        const savedTimer = localStorage.getItem('otpTimer');
        const endTime = parseInt(savedTimer, 10);

        if (endTime && endTime > Date.now()) {
            setTimer(Math.ceil((endTime - Date.now()) / 1000));
        } else {
            // Reset timer if not saved or expired
            localStorage.removeItem('otpTimer');
            setTimer(60);
            setIsResendEnabled(false);
        }

        const countdown = timer > 0 ? setInterval(() => {
            setTimer((prev) => {
                const newTime = prev - 1;
                localStorage.setItem('otpTimer', Date.now() + newTime * 1000);
                if (newTime <= 0) {
                    setIsResendEnabled(true);
                    localStorage.removeItem('otpTimer');
                }
                return newTime;
            });
        }, 1000) : null;

        return () => {
            if (countdown) clearInterval(countdown);
        };
    }, [timer]);

    const handleSendOtp = async () => {
        try {
            const response = await fetch('http://localhost:8080/verification/send-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                setIsCodeSent(true);
                setIsResendEnabled(false);
                setTimer(60); // Reset timer
                localStorage.setItem('otpTimer', Date.now() + 60 * 1000);
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Failed to send OTP. Please try again.');
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/verification/verify-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email, otp }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                localStorage.removeItem('otpTimer'); // Clear timer on successful verification
                navigate('/reset-password', { state: { email: state.email } });
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    const handleResendOtp = async () => {
        try {
            setIsResendEnabled(false);
            setTimer(60); 
            localStorage.setItem('otpTimer', Date.now() + 60 * 1000);
            const response = await fetch('http://localhost:8080/verification/resend-otp', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Failed to resend OTP. Please try again.');
        }
    };

    useEffect(() => {
        if (!isCodeSent) {
            handleSendOtp(); // Automatically send the OTP when the component mounts
        }
    }, [isCodeSent]);

    return (
        <div className="otp-wrapper">
            <div>
                <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            </div>
            <h1 className="otp-title">Verification Code</h1>
            <div className="otp-timer">
                <p>We've sent a verification code to your registered email address.</p>
                <div className="timer">
                    <p>Time Remaining: {timer} s</p>
                </div>
            </div>
            <form onSubmit={handleSubmit}>
                <OtpInput
                    length={6}
                    onOtpSubmit={(otp) => setOtp(otp)}
                    onOtpComplete={(complete) => setIsComplete(complete)}
                />
                <div className="otp-buttons">
                    <button
                        type="button"
                        className="otp-btn send"
                        onClick={handleSendOtp}
                        disabled={isCodeSent || timer > 0}
                    >
                        Send Code
                    </button>
                    {timer === 0 ? (
                        <>
                            <div className="otp-message">
                                <p>Didn't receive the code?</p>
                            </div>
                            <button
                                type="button"
                                className="otp-btn resend"
                                onClick={handleResendOtp}
                            >
                                Resend Code
                            </button>
                        </>
                    ) : (
                        <>
                            <div className="otp-message">
                                <br />
                            </div>
                            <button
                                type="submit"
                                className={`otp-btn verify ${isComplete ? 'active' : ''}`}
                                disabled={!isComplete}
                            >
                                Verify Code
                            </button>
                        </>
                    )}
                </div>
            </form>
            <ToastContainer />
        </div>
    );
}

export default VerifyCodeConfirmAccount;
