// utils.js
import { toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export const handleSuccess = (msg) => {
    toast.success(msg, {
        position: 'top-center', // Set position to top-center
        autoClose: 3000, // Automatically close after 3 seconds
        hideProgressBar: true, // Hide the progress bar for a cleaner look
        closeOnClick: true, // Close the toast when clicked
        pauseOnHover: true, // Pause the timer when hovered
        draggable: true, // Allow the toast to be draggable
        progress: undefined,
        theme: "light",
        className: 'apple-toast-success', // Custom class for styling
    });
};

export const handleError = (msg) => {
    toast.error(msg, {
        position: 'top-center', // Set position to top-center
        autoClose: 3000, // Automatically close after 3 seconds
        hideProgressBar: true, // Hide the progress bar for a cleaner look
        closeOnClick: true, // Close the toast when clicked
        pauseOnHover: true, // Pause the timer when hovered
        draggable: true, // Allow the toast to be draggable
        progress: undefined,
        theme: "light",
        className: 'apple-toast-error', // Custom class for styling
    });
};
