import { Navigate, Route, Routes } from 'react-router-dom';
import { useState } from 'react';
import Login from './pages/Login';
import Signup from './pages/Signup';
import Home from './pages/Home';
import UserProfile from './pages/UserProfile';
import FindUserForgotPassword from './components/ForgotPassword/FindUserForgotPassword';
import SentOtpForgotPassword from './components/ForgotPassword/SentOtpForgotPassword';
import ResetPassword from './components/ForgotPassword/ResetPassword';
import RefrshHandler from './utils/RefreshHandler';
import 'react-toastify/dist/ReactToastify.css';
import VerifyCodeConfirmAccount from './pages/VerifyCodeConfirmAccount'; 
import { useLocation } from 'react-router-dom';
import ProtectedRoute from './components/ProtectedRoute';

function App() {
    const location = useLocation();
    console.log('Current location:', location.pathname);

    const [isAuthenticated, setIsAuthenticated] = useState(false);
    console.log('Is authenticated:', isAuthenticated);

    return (
        <div className="App">
            <RefrshHandler setIsAuthenticated={setIsAuthenticated} />
            <Routes>
                <Route path='/' element={<Navigate to="/login" />} />
                <Route path='/login' element={<Login />} />
                <Route path='/signup' element={<Signup />} />
                <Route path="/verify-email" element={
                    <ProtectedRoute component={VerifyCodeConfirmAccount} />
                } />
                <Route path='/home' element={
                    <ProtectedRoute component={Home} />
                } />
                <Route path="/user-profile" element={
                    <ProtectedRoute component={UserProfile} />
                } />
                <Route path='/find-user' element={
                    <ProtectedRoute component={FindUserForgotPassword} />
                } />
                <Route path='/forgot-password/verification-code' element={
                    <ProtectedRoute component={SentOtpForgotPassword} />
                } />
                <Route path='/forgot-password/reset-password' element={
                    <ProtectedRoute component={ResetPassword} />
                } />
            </Routes>
        </div>
    );
}

export default App;
