import React, { useCallback, useEffect, useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleError } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../assets/style/ReactToastifyCustom.css';
import '../assets/style/home.css';
import '../assets/style/loading.css';
import LoadingOverlay from '../components/LoadingOverlay';
import ServiceCard from '../components/ServiceCard';

function Home() {
    const [loggedInUser, setLoggedInUser] = useState({});
    const [loading, setLoading] = useState(true);
    const [fadeIn, setFadeIn] = useState(true);
    const navigate = useNavigate();

    const fetchLoggedInUser = useCallback(async () => {
        setLoading(true);
        try {
            const url = "http://localhost:8080/Users/me";
            const headers = {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            };
            const response = await fetch(url, headers);
            const result = await response.json();
            if (response.ok) {
                setLoggedInUser(result);
            } else {
                handleError(result.message || 'Failed to fetch user data. Please try again.');
                navigate('/login');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
            navigate('/login');
        } finally {
            setLoading(false);
        }
    }, [navigate]);

    useEffect(() => {
        fetchLoggedInUser();

        const timeoutId = setTimeout(() => {
            setFadeIn(false);
        }, 500);

        return () => clearTimeout(timeoutId);
    }, [fetchLoggedInUser]); // Use fetchLoggedInUser as a dependency

    const services = [
        { name: 'Chat', icon: 'chat-icon.svg' },
        { name: 'AI Assistant', icon: 'ai-icon.svg' },
        { name: 'Photo Editing', icon: 'photo-editing-icon.svg' },
        { name: 'Social Media', icon: 'social-media-icon.svg' },
        { name: 'Video Streaming', icon: 'video-streaming-icon.svg' },
        { name: 'Cloud Storage', icon: 'cloud-storage-icon.svg' },
        { name: 'Music Streaming', icon: 'music-streaming-icon.svg' },
        { name: 'E-commerce', icon: 'e-commerce-icon.svg' },
        { name: 'Productivity', icon: 'productivity-icon.svg' },
        { name: 'Fitness Tracking', icon: 'fitness-tracking-icon.svg' },
    ];

    if (loading) {
        return <LoadingOverlay loading={loading} fadeOut={false} />;
    }

    return (
        <div className={`home-container ${fadeIn ? 'fade-in' : ''}`}>
            <header className="home-header">
                <div className="user-profile">
                    <Link to="/user-profile" className="user-avatar">
                        <img src='/avatar.png' alt='Profile' />
                    </Link>
                    <h2 className="user-name">{loggedInUser.name}</h2>
                </div>
            </header>
            <main className="home-main">
                <h1 className="welcome-message">Welcome to Your Dashboard</h1>
                <div className="services-container">
                    <h2 className="services-title">Popular Services</h2>
                    <div className="services-grid">
                        {services.map((service, index) => (
                            <ServiceCard key={index} name={service.name} icon={service.icon} />
                        ))}
                    </div>
                </div>
            </main>
            <ToastContainer />
        </div>
    );
}

export default Home;
