import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function FindUserForgotPassword() {
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [user, setUser] = useState(null);
    const [loading, setLoading] = useState(false);
    const [showLoading, setShowLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        if (showLoading) {
            setTimeout(() => {
                setShowLoading(false);
                setFadeIn(true);
            }, 1000); // Show loading spinner for 1 second
        }
    }, [showLoading]);

    const handleSearch = async (event) => {
        event.preventDefault();
        setLoading(true);
        setShowLoading(true);
        setFadeIn(false);

        try {
            const response = await fetch('http://localhost:8080/auth/forgot-password/find-user', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ emailOrUsername }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                setUser(result.user);
            } else {
                handleError(result.message);
                setUser(null);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    const handleProceed = () => {
        if (user) {
            navigate('/forgot-password/send-otp', { state: { email: user.email } });
        }
    };

    return (
        <div className="find-user-container container">
            <h1>Forgot Password</h1>
            <form onSubmit={handleSearch} className="search-form">
                <div className="input-wrapper">
                    <input
                        type="text"
                        placeholder="Enter your email or username..."
                        value={emailOrUsername}
                        onChange={(e) => setEmailOrUsername(e.target.value)}
                        required
                    />
                    <button type="submit" className="search-button" disabled={loading}>
                        <i className="search-icon">&#128269;</i>
                    </button>
                </div>
            </form>

            {showLoading && (
                <div className="loading-overlay">
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}

            <div className={`found-user-container ${fadeIn ? 'fade-in' : ''}`}>
                {user ? (
                    <div
                        className="user-info-to-forgot-password"
                        onClick={handleProceed} 
                        style={{ cursor: 'pointer' }}
                    >
                        <div className="devof-user-info-to-forgot-password">
                            <img src={user.profilePicture} alt={user.name} />
                            <div/>
                            <div className="devof-user-info-to-forgot-password">
                                <h3>{user.name}</h3>
                                <p>Username: {user.username}</p>
                            </div>
                        </div>
                    </div>
                ) : (
                    <div className="user-info-to-forgot-password" >
                      <p>Search your Account by Email Or Username.</p>
                    </div>
                )}
            </div>

            <ToastContainer />
        </div>
    );
}

export default FindUserForgotPassword;
