import React, { useState, useRef } from "react";
import "../assets/style/ExtensionStyle/AvatarUploadPopup.css";
import { uploadAvatar } from "../utils/ApiService";
import LoadingOverlay from "../components/LoadingOverlay"; // Import the LoadingOverlay component

const AvatarUploadPopup = ({ onClose }) => {
  const fileInputRef = useRef(null);
  const [loading, setLoading] = useState(false); // State to manage loading

  const handleUploadClick = () => {
    if (fileInputRef.current) {
      fileInputRef.current.click();
    }
  };

  const handleFileChange = async (event) => {
    const file = event.target.files[0];
    if (file) {
      setLoading(true); // Show loading overlay
      try {
        await uploadAvatar(file);
        onClose(); // Close popup on successful upload
      } catch (error) {
        console.error("Upload failed:", error);
      } finally {
        setLoading(false); // Hide loading overlay
      }
    }
  };

  return (
    <div className="avatar-upload-popup-overlay">
      <div className="avatar-upload-popup">
        <LoadingOverlay loading={loading} /> 
        <p className="popup-title">Change Avatar</p>
        <div className="popup-options">
          <button className="popup-option-button" onClick={handleUploadClick}>
            Upload
          </button>
          <input
            type="file"
            accept="image/*"
            onChange={handleFileChange}
            className="file-input"
            ref={fileInputRef}
            style={{ display: "none" }} // Hide the input element
          />
          <button className="popup-option-button">Remove Avatar</button>
          {/* <button className="popup-option-button">Select Avatar</button> */}
          <button className="popup-option-button" onClick={onClose}>
            Cancel
          </button>
        </div>
      </div>
    </div>
  );
};

export default AvatarUploadPopup;
