// frontend/src/pages/Login.jsx

import React, { useEffect, useState, useContext } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../assets/style/styleutils/ReactToastifyCustom.css';
import '../assets/style/styleutils/loading.css';
import '../assets/style/styleutils/animations.css';
import '../assets/style/PagesStyle/Login.css';
import LoadingOverlay from '../components/LoadingOverlay';
import { AuthContext } from '../contexts/AuthContext';

function Login() {
    const { login } = useContext(AuthContext);
    const [emailOrUsername, setEmailOrUsername] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    useEffect(() => {
        const checkExistingAuth = async () => {
            const isAuth = await checkAuthentication();
            if (isAuth) {
                navigate('/home', { replace: true });
            }
        };
        checkExistingAuth();
    }, [navigate]);

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);
        try {
            await login({ emailOrUsername, password });
            handleSuccess('Login successful!');
            navigate('/home', { replace: true });
        } catch (err) {
            handleError('Login failed. Please check your credentials.');
        } finally {
            setLoading(false);
        }
    };

    const handleForgotPassword = (event) => {
        event.preventDefault();
        navigate('/find-user'); 
    };

    return (
        <>
            <div className={`login-page-wrapper fade-in`}>
                <div className={`login-page-container general-page-container`}>
                    <h1>Login</h1>
                    <form onSubmit={handleSubmit}>
                        <input
                            type='text'
                            id='emailOrUsername'
                            placeholder=' '
                            value={emailOrUsername}
                            onChange={(e) => setEmailOrUsername(e.target.value)}
                            required
                        />
                        <label htmlFor='emailOrUsername'>Email or Username</label>
                        <input
                            type='password'
                            id='password'
                            placeholder=' '
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='password'>Password</label>
                        <button type='submit'>Login</button>
                        <Link to="#" onClick={handleForgotPassword}>Forgot Password?</Link>
                    </form>
                </div>
                <LoadingOverlay loading={loading} />
            </div>
            <ToastContainer />
        </>
    );
}

export default Login;
