import React, { useState, useRef, useEffect } from 'react';
import '../assets/style/PromptInput.css';

const MAX_CHARS = 1000;

const PromptInput = () => {
  const [message, setMessage] = useState('');
  const [isFocused, setIsFocused] = useState(false);
  const [showOptions, setShowOptions] = useState(false);
  const textareaRef = useRef(null);

  const handleSendMessage = () => {
    console.log('Sending message:', message);
    setMessage('');
  };

  const handleInputChange = (e) => {
    const inputValue = e.target.value;
    if (inputValue.length <= MAX_CHARS) {
      setMessage(inputValue);
    }
  };

  const toggleOptions = () => {
    setShowOptions(!showOptions);
  };

  useEffect(() => {
    if (textareaRef.current) {
      textareaRef.current.style.height = '5px';
      const scrollHeight = textareaRef.current.scrollHeight;
      textareaRef.current.style.height = `${Math.min(scrollHeight, 200)}px`;
    }
  }, [message]);

  return (
    <div className={`prompt-container ${isFocused ? 'focused' : ''}`}>
      <div className="input-wrapper">
        <button className="document-btn" id="add-document-btn" onClick={toggleOptions}>
          <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="#5E5CE6" className="bi bi-plus-circle-fill" viewBox="0 0 16 16">
            <path d="M16 8A8 8 0 1 1 0 8a8 8 0 0 1 16 0M8.5 4.5a.5.5 0 0 0-1 0v3h-3a.5.5 0 0 0 0 1h3v3a.5.5 0 0 0 1 0v-3h3a.5.5 0 0 0 0-1h-3z"/>
          </svg>
        </button>

        {showOptions && (
          <div className="document-options">
            <button className="option-btn camera">Camera</button>
            <button className="option-btn photos">Photos</button>
            <button className="option-btn location">Location</button>
            <button className="option-btn music">Music</button>
            <button className="option-btn file">File</button>
          </div>
        )}

        <textarea
          ref={textareaRef}
          id="message-input"
          className="message-input"
          placeholder="Send a message..."
          value={message}
          onChange={handleInputChange}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          onKeyPress={(e) => {
            if (e.key === 'Enter' && !e.shiftKey) {
              e.preventDefault();
              handleSendMessage();
            }
          }}
        />

        <button
          className="send-btn"
          id="send-btn"
          title="Send Message"
          onClick={handleSendMessage}
          disabled={!message.trim()}
        >
          <svg xmlns="http://www.w3.org/2000/svg" width="30" height="30" fill="#5E5CE6" className="bi bi-arrow-up-circle-fill" viewBox="0 0 16 16">
            <path d="M16 8A8 8 0 1 0 0 8a8 8 0 0 0 16 0m-7.5 3.5a.5.5 0 0 1-1 0V5.707L5.354 7.854a.5.5 0 1 1-.708-.708l3-3a.5.5 0 0 1 .708 0l3 3a.5.5 0 0 1-.708.708L8.5 5.707z"/>
          </svg>
        </button>
      </div>
      <div className="char-count">{message.length}/{MAX_CHARS}</div>
    </div>
  );
};

export default PromptInput;
