// FindUserForgotPassword.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function FindUserForgotPassword() {
    const [identifier, setIdentifier] = useState(''); // Can be email or username
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleSearch = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:8080/auth/forgotPassword', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ identifier })
            });
            const result = await response.json();
            if (response.ok) {
                navigate('/sentOtpForgotPassword');
            } else {
                handleError(result.message || 'User not found');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="find-user-forgot-password-container">
            <h1>Find Your Account</h1>
            <input
                type="text"
                placeholder="Enter your email or username"
                value={identifier}
                onChange={(e) => setIdentifier(e.target.value)}
                required
            />
            <button onClick={handleSearch} disabled={loading}>Search</button>
            <ToastContainer />
        </div>
    );
}

export default FindUserForgotPassword;
