//Signup.js

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Signup() {
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [confirmPassword, setConfirmPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(true); 
    const [fadeOut, setFadeOut] = useState(false);
    const navigate = useNavigate();

    

    const handleSubmit = async (event) => {
        event.preventDefault();

        if (password !== confirmPassword) {
            handleError('Passwords do not match');
            return;
        }

        setLoading(true);
        setFadeOut(false);


        useEffect(() => {
            fetchLoggedInUser();
    
            // Trigger fade-in effect after component mounts
            const timeoutId = setTimeout(() => {
                setFadeIn(false);
            }, 500); // Delay to match the fade-in animation duration
    
            return () => clearTimeout(timeoutId);
        }, []);

        setTimeout(async () => {
            setFadeOut(true);
            setTimeout(async () => {
                try {
                    const response = await fetch("http://localhost:8080/auth/signup", {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ name, email, password }),
                    });
                    const result = await response.json();
                    if (response.ok) {
                        handleSuccess('Signup successful! Redirecting...');
                        setTimeout(() => navigate('/login'), 2000);
                    } else {
                        handleError(result.message || 'An error occurred during signup. Please try again.');
                    }
                } catch (err) {
                    handleError('Network error. Please check your connection and try again.');
                } finally {
                    setLoading(false);
                }
            }, 250);
        }, 1000);
    };

    return (
        <div className={`home-container container ${fadeIn ? 'fade-in' : ''}`}>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Sign up</h1>
            <form onSubmit={handleSubmit}>
                <div className='form-group'>
                    <input
                        type='text'
                        id='name'
                        placeholder='Enter your name...'
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        required
                    />
                    <label htmlFor='name' className='form-label'>Name</label>
                </div>
                <div className='form-group'>
                    <input
                        type='email'
                        id='email'
                        placeholder='Enter your email...'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>

                <div className='form-group-container'>
                    <div className='form-group'>
                        <input
                            type='password'
                            id='password'
                            placeholder='Enter your password...'
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='password' className='form-label'>Password</label>
                    </div>

                    <div className='form-group'>
                        <input
                            type='password'
                            id='confirmPassword'
                            placeholder='Confirm your password...'
                            value={confirmPassword}
                            onChange={(e) => setConfirmPassword(e.target.value)}
                            required
                        />
                        <label htmlFor='confirmPassword' className='form-label'>Confirm Password</label>
                    </div>
                </div>

                <button className='signup-button' type='submit'>Sign up</button>
                <span>Already have an account? <Link to="/login" className='pages-link'>Login</Link></span>
            </form>
            <ToastContainer />
            {loading && (
                <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Signup;
