// Login.js 

import React, { useState } from 'react';
import { useNavigate, Link } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const [fadeIn, setFadeIn] = useState(true); 
    const [fadeOut, setFadeOut] = useState(false);
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();
        setLoading(true);
        setFadeOut(false);

        useEffect(() => {
            
            // Trigger fade-in effect after component mounts
            const timeoutId = setTimeout(() => {
                setFadeIn(false);
            }, 500); 
    
            return () => clearTimeout(timeoutId);
        }, []);
    

        setTimeout(async () => {
            setFadeOut(true);
            setTimeout(async () => {
                try {
                    const response = await fetch("http://localhost:8080/auth/login", {
                        method: 'POST',
                        headers: {
                            'Content-Type': 'application/json',
                        },
                        body: JSON.stringify({ email, password }),
                    });
                    const result = await response.json();
                    if (response.ok) {
                        localStorage.setItem('token', result.accessToken);
                        localStorage.setItem('refreshToken', result.refreshToken);
                        localStorage.setItem('loggedInUser', JSON.stringify(result));
                        handleSuccess('Login successful! Redirecting...');
                        setTimeout(() => navigate('/home'), 500);
                    } else {
                        handleError(result.message || 'Incorrect email or password. Please try again.');
                    }
                } catch (err) {
                    handleError('Network error. Please check your connection and try again.');
                } finally {
                    setLoading(false);
                }
            }, 250);
        }, 1000);
    };

    return (
        <div className={`home-container container ${fadeIn ? 'fade-in' : ''}`}>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Login</h1>
            <form onSubmit={handleSubmit}>
                <div className='form-group'>
                    <input
                        type='email'
                        id='email'
                        placeholder='Enter your email...'
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>
                <div className='form-group'>
                    <input
                        type='password'
                        id='password'
                        placeholder='Enter your password...'
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <label htmlFor='password' className='form-label'>Password</label>
                </div>
                <button type='submit'>Login</button>
                <Link to="/ForgotPassword" className='pages-link'>Forgot Password?</Link>
                <span>Don't have an account? <Link to="/signup" className='pages-link'>Signup</Link></span>
            </form>
            <ToastContainer />
            {loading && (
                <div className={`loading-overlay ${fadeOut ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Login;
