import React, { useCallback, useState, useEffect } from 'react';
import MessageReactions from './MessageReactions';
import "../../../assets/style/ServicesStyle/ChatServicesStyle/SelectedUserMessages.css";

const MessageOptions = ({ message, onReply, onCopy, onEdit, onUnsend, onForward, onBump, onPin, onMore }) => {
  return (
    <div className="message-options">
      <button onClick={(e) => onReply(e)}>Reply</button>
      <button onClick={(e) => onCopy(e)}>Copy</button>
      {message.sender === "user" && (
        <>
          <button onClick={(e) => onEdit(e)}>Edit</button>
          <button onClick={(e) => onUnsend(e)}>Unsend</button>
        </>
      )}
      <button onClick={(e) => onForward(e)}>Forward</button>
      <button onClick={(e) => onBump(e)}>Bump</button>
      <button onClick={(e) => onPin(e)}>Pin</button>
      <button onClick={(e) => onMore(e)}>More</button>
    </div>
  );
};

const SelectedUserMessages = ({ selectedUser, messages, formatTime, reactions, showReactions, handleReaction, toggleReactionMenu, onEditMessage }) => {
  const [showOptions, setShowOptions] = useState({});
  const [isMobile, setIsMobile] = useState(false);
  const [editingMessageId, setEditingMessageId] = useState(null);

  useEffect(() => {
    const checkMobile = () => {
      setIsMobile(window.innerWidth <= 768);
    };
    checkMobile();
    window.addEventListener('resize', checkMobile);
    return () => window.removeEventListener('resize', checkMobile);
  }, []);

  const toggleOptions = (messageId, e) => {
    e.stopPropagation();
    setShowOptions(prev => ({
      ...prev,
      [messageId]: !prev[messageId]
    }));
  };

  const handleOptionClick = (e, option, message) => {
    e.stopPropagation();
    console.log(`${option} clicked for message:`, message);
    
    if (option === 'edit') {
      setEditingMessageId(message.id);
    } else {
      // Implement the logic for other options here
    }
    
    setShowOptions(prev => ({ ...prev, [message.id]: false }));
  };

  const handleEditSubmit = (messageId, newText) => {
    onEditMessage(messageId, newText);
    setEditingMessageId(null);
  };

  const renderMessageGroups = useCallback(() => {
    if (!selectedUser || !messages[selectedUser.id]) {
      return null;
    }

    return messages[selectedUser.id]
      .reduce((groups, msg, index, array) => {
        const prevMsg = array[index - 1];
        const timeDiff = prevMsg ? msg.timestamp - prevMsg.timestamp : Infinity;
        const isNewGroup =
          !prevMsg || timeDiff > 30000 || prevMsg.sender !== msg.sender;

        if (isNewGroup) {
          groups.push([msg]);
        } else {
          groups[groups.length - 1].push(msg);
        }
        return groups;
      }, [])
      .map((group, groupIndex) => (
        <div
          key={groupIndex}
          className={`message-group ${
            group[0].sender === "user" ? "user-group" : "other-group"
          }`}
        >
          <div className="message-timestamp-center">
            {formatTime(group[0].timestamp)}
          </div>
          <div className="message-group-content">
            {group[0].sender !== "user" && (
              <img
                src={selectedUser.avatar}
                alt={selectedUser.name}
                className="message-avatar"
              />
            )}
            <div className="message-bubble-group">
              {group.map((msg, msgIndex) => (
                <div
                  key={msgIndex}
                  className="message-bubble"
                >
                  {msg.type === "text" && (
                    <div className="message-content">{msg.text}</div>
                  )}
                  {msg.type === "image" && (
                    <img
                      src={URL.createObjectURL(msg.file)}
                      alt="Sent image"
                      className="message-image"
                    />
                  )}
                  {msg.type === "file" && (
                    <div className="message-file">
                      <a
                        href={URL.createObjectURL(msg.file)}
                        download={msg.file.name}
                      >
                        {msg.file.name}
                      </a>
                    </div>
                  )}
                  <MessageReactions
                    messageId={msg.id}
                    reaction={reactions[msg.id]}
                    showReactions={showReactions[msg.id]}
                    onReaction={handleReaction}
                    onToggleReactionMenu={toggleReactionMenu}
                  />
                  {!isMobile && (
                    <button className="message-options-toggle" onClick={(e) => toggleOptions(msg.id, e)}>
                      â‹®
                    </button>
                  )}
                  {showOptions[msg.id] && (
                    <MessageOptions
                      message={msg}
                      onReply={(e) => handleOptionClick(e, 'reply', msg)}
                      onCopy={(e) => handleOptionClick(e, 'copy', msg)}
                      onUnsend={(e) => handleOptionClick(e, 'unsend', msg)}
                      onForward={(e) => handleOptionClick(e, 'forward', msg)}
                      onBump={(e) => handleOptionClick(e, 'bump', msg)}
                      onPin={(e) => handleOptionClick(e, 'pin', msg)}
                      onMore={(e) => handleOptionClick(e, 'more', msg)}
                    />
                  )}
                </div>
              ))}
            </div>
          </div>

          {/* Message status displayed below the message bubble */}
          {group[group.length - 1].sender === "user" && (
            <div className="message-status">
              {group[group.length - 1].status === "sent" && <span>sent</span>}
              {group[group.length - 1].status === "delivered" && (
                <span>delivered</span>
              )}
              {group[group.length - 1].status === "seen" && (
                <div className="seen-status">
                  <img
                    src={selectedUser.avatar}
                    alt={selectedUser.name}
                    className="seen-avatar"
                  />
                  <span className="seen-text"></span>
                </div>
              )}
            </div>
          )}
        </div>
      ));
  }, [messages, selectedUser, formatTime, reactions, showReactions, handleReaction, toggleReactionMenu, showOptions, isMobile]);

  return (
    <div className="chat-messages-container">
      {renderMessageGroups()}
    </div>
  );
};

export default SelectedUserMessages;
