import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Login() {
    const [loginInfo, setLoginInfo] = useState({ email: '', password: '' });
    const [loading, setLoading] = useState(false);
    const [loadingCompleted, setLoadingCompleted] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginInfo(prevState => ({ ...prevState, [name]: value }));
    }

    const handleLogin = async (e) => {
        e.preventDefault();
        const { email, password } = loginInfo;
        if (!email || !password) {
            return handleError('Email and password are required');
        }
        setLoading(true);
        setLoadingCompleted(false);

        try {
            const url = `http://localhost:8080/auth/login`;
            const response = await fetch(url, {
                method: "POST",
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify(loginInfo)
            });
            const result = await response.json();

            setLoading(false);
            setLoadingCompleted(true);

            if (response.status === 403) {
                handleError(result.message);
            } else if (response.status === 200) {
                handleSuccess(result.message);
                localStorage.setItem('accessToken', result.accessToken);
                localStorage.setItem('refreshToken', result.refreshToken);
                navigate('/');
            } else {
                handleError(result.message);
            }
        } catch (err) {
            setLoading(false);
            setLoadingCompleted(true);
            handleError(err.message || 'An error occurred');
        }
    }

    return (
        <div className='container'>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div className='form-group'>
                    <input onChange={handleChange} type='email' name='email' id='email' autoFocus placeholder='Enter your email...' value={loginInfo.email} />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>
                <div className='form-group'>
                    <input onChange={handleChange} type='password' name='password' id='password' placeholder='Enter your password...' value={loginInfo.password} />
                    <label htmlFor='password' className='form-label'>Password</label>
                </div>
                <button type='submit' className='btn'>Login</button>
                <p>Don't have an account? <Link to='/signup'>Sign up</Link></p>
            </form>
            <ToastContainer />
            {loading && !loadingCompleted && <div className="loading-popup"><img src='/apple-loading.gif' alt='Loading...' /></div>}
        </div>
    )
}

export default Login;
