import { Check, X } from "lucide-react";

const PasswordCriteria = ({ password }) => {
  // Define criteria with check for password validity
  const criteria = [
    { label: "At least 8 characters", met: password.length >= 6 },
    { label: "Contains uppercase letter", met: /[A-Z]/.test(password) },
    { label: "Contains lowercase letter", met: /[a-z]/.test(password) },
    { label: "Contains a number", met: /\d/.test(password) },
    { label: "Contains special character", met: /[^A-Za-z0-9]/.test(password) },
  ];

  return (
    <div className='mt-2 space-y-1'>
      {criteria.map(({ label, met }) => (
        <div key={label} className='flex items-center text-xs'>
          {met ? (
            <Check className='w-4 h-4 text-green-500 mr-2' />
          ) : (
            <X className='w-4 h-4 text-gray-500 mr-2' />
          )}
          <span className={met ? "text-green-500" : "text-gray-400"}>{label}</span>
        </div>
      ))}
    </div>
  );
};

const PasswordStrengthMeter = ({ password }) => {
  // Compute password strength
  const getStrength = (pass) => {
    let strength = 0;
    if (pass.length >= 8) strength++;
    if (/[a-z]/.test(pass) && /[A-Z]/.test(pass)) strength++;
    if (/\d/.test(pass)) strength++;
    if (/[^a-zA-Z\d]/.test(pass)) strength++;
    return strength;
  };

  const strength = getStrength(password);

  // Map strength levels to colors and text
  const strengthLevels = {
    0: { color: "bg-red-500", text: "Very Weak" },
    1: { color: "bg-red-400", text: "Weak" },
    2: { color: "bg-yellow-500", text: "Fair" },
    3: { color: "bg-yellow-400", text: "Good" },
    4: { color: "bg-green-500", text: "Strong" }
  };

  const { color, text } = strengthLevels[strength] || strengthLevels[0];

  return (
    <div className='mt-2'>
      <div className='flex justify-between items-center mb-1'>
        <span className='text-xs text-gray-400'>Password strength</span>
        <span className='text-xs text-gray-400'>{text}</span>
      </div>
      <div className='flex space-x-1'>
        {[...Array(4)].map((_, index) => (
          <div
            key={index}
            className={`h-1 w-1/4 rounded-full transition-colors duration-300 
              ${index < strength ? color : "bg-gray-600"}
            `}
          />
        ))}
      </div>
      <PasswordCriteria password={password} />
    </div>
  );
};

export default PasswordStrengthMeter;
