import React, { useEffect, useState } from "react";
import { useNavigate, Link } from "react-router-dom";
import {
  handleSuccess,
  handleError,
  ToastContainer,
} from "../utils/ReactToastify";
import "../utils/ReactToastifyCustom.css";
import "../utils/style/animations.css";
import "../utils/loading.css";

function Signup() {
  const [firstName, setFirstName] = useState("");
  const [lastName, setLastName] = useState("");
  const [email, setEmail] = useState("");
  const [password, setPassword] = useState("");
  const [confirmPassword, setConfirmPassword] = useState("");
  const [phone, setPhone] = useState("");
  const [dobDay, setDobDay] = useState("");
  const [dobMonth, setDobMonth] = useState("");
  const [dobYear, setDobYear] = useState("");
  const [gender, setGender] = useState("");
  const [loading, setLoading] = useState(false);
  const [fadeIn, setFadeIn] = useState(true);
  const [fadeOut, setFadeOut] = useState(false);
  const navigate = useNavigate();

  useEffect(() => {
    const timeoutId = setTimeout(() => {
      setFadeIn(false);
    }, 500);
    return () => clearTimeout(timeoutId);
  }, []);

  const handleSubmit = async (event) => {
    event.preventDefault();

    if (password !== confirmPassword) {
      handleError("Passwords do not match");
      return;
    }

    setLoading(true);
    setFadeOut(false);

    setTimeout(async () => {
      setFadeOut(true);
      setTimeout(async () => {
        try {
          const response = await fetch("http://localhost:8080/auth/signup", {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              firstName,
              lastName,
              email,
              password,
              phone,
              dob: {
                day: dobDay,
                month: dobMonth,
                year: dobYear,
              },
              gender,
            }),
          });
          const result = await response.json();
          if (response.ok) {
            // Save tokens and user info to localStorage
            localStorage.setItem("token", result.accessToken);
            localStorage.setItem("refreshToken", result.refreshToken);
            localStorage.setItem(
              "loggedInUser",
              JSON.stringify({
                name: `${result.firstName} ${result.lastName}`,
                email: result.email,
              })
            );

            handleSuccess("Signup successful!");
            setTimeout(() => navigate("/home"), 500); // Redirect to home page
          } else {
            handleError(
              result.message ||
                "An error occurred during signup. Please try again."
            );
          }
        } catch (err) {
          handleError(
            "Network error. Please check your connection and try again."
          );
        } finally {
          setLoading(false);
        }
      }, 250);
    }, 1000);
  };

  return (
    <div className={`signup-container container ${fadeIn ? "fade-in" : ""}`}>
      <img src="/app-icon.ico" alt="App Icon" className="app-icon" />
      <h1>Sign up</h1>
      <form onSubmit={handleSubmit}>

      <div className="form-group-container">

        <div className="singup-form-group form-group">
          <input
            type="text"
            id="firstName"
            placeholder=""
            value={firstName}
            onChange={(e) => setFirstName(e.target.value)}
            required
          />
          <label htmlFor="firstName" className="form-label">
            First Name
          </label>
        </div>

        <div className="singup-form-group form-group">
          <input
            type="text"
            id="lastName"
            placeholder=""
            value={lastName}
            onChange={(e) => setLastName(e.target.value)}
            required
          />
          <label htmlFor="lastName" className="form-label">
            Last Name
          </label>
        </div>
        </div>

        <div className="singup-form-group form-group">
          <input
            type="email"
            id="email"
            placeholder=""
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            required
          />
          <label htmlFor="email" className="form-label">
            Email
          </label>
        </div>

        <div className="form-group-container">
          <div className="form-group">
            <input
              type="password"
              id="password"
              placeholder=""
              value={password}
              onChange={(e) => setPassword(e.target.value)}
              required
            />
            <label htmlFor="password" className="form-label">
              Password
            </label>
          </div>

          <div className="form-group">
            <input
              type="password"
              id="confirmPassword"
              placeholder=""
              value={confirmPassword}
              onChange={(e) => setConfirmPassword(e.target.value)}
              required
            />
            <label htmlFor="confirmPassword" className="form-label">
              Confirm Password
            </label>
          </div>
        </div>

        <div className="form-group-container">
          <div className="form-group">
            <input
              
              type="tel"
              id="phone"
              placeholder=""
              value={phone}
              onChange={(e) => setPhone(e.target.value)}
              required
            />
            <label htmlFor="phone" className="form-label">
              Phone Number
            </label>
          </div>
          <div className="form-group">
            <select
              id="gender"
              value={gender}
              onChange={(e) => setGender(e.target.value)}
              required
            >
              <option value="" disabled>
                Select Gender
              </option>
              <option value="male">Male</option>
              <option value="female">Female</option>
              <option value="other">Other</option>
            </select>
            {/* <label htmlFor="gender" className="form-label">
              Gender
            </label> */}
          </div>
        </div>

        <div className="form-group-container dob">
          <div className="form-group">
            <select
              id="dobDay"
              value={dobDay}
              onChange={(e) => setDobDay(e.target.value)}
              required
            >
              <option value="" disabled>
                Day
              </option>
              {Array.from({ length: 31 }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {i + 1}
                </option>
              ))}
            </select>
            {/* <label htmlFor="dobDay" className="form-label">
              Day
            </label> */}
          </div>

          <div className="form-group">
            <select
              id="dobMonth"
              value={dobMonth}
              onChange={(e) => setDobMonth(e.target.value)}
              required
            >
              <option value="" disabled>
                Month
              </option>
              {Array.from({ length: 12 }, (_, i) => (
                <option key={i + 1} value={i + 1}>
                  {new Date(0, i).toLocaleString("default", { month: "long" })}
                </option>
              ))}
            </select>
            {/* <label htmlFor="dobMonth" className="form-label">
              Month
            </label> */}
          </div>

          <div className="form-group">
            <select
              id="dobYear"
              value={dobYear}
              onChange={(e) => setDobYear(e.target.value)}
              required
            >
              <option value="" disabled>
                Year
              </option>
              {Array.from({ length: 100 }, (_, i) => (
                <option key={2024 - i} value={2024 - i}>
                  {2024 - i}
                </option>
              ))}
            </select>
            {/* <label htmlFor="dobYear" className="form-label">
              Year
            </label> */}
          </div>
        </div>



        <button className="signup-button" type="submit">
          Sign up
        </button>
        <span>
          Already have an account?{" "}
          <Link to="/login" className="pages-link">
            Login
          </Link>
        </span>
      </form>

      <span className="terms">
        By creating an account, you agree to our
        <Link to="/terms" className="terms-pages-link">
          {" "}
          Terms of Use
        </Link>
        ,
        <Link to="/privacy-policy" className="terms-pages-link">
          {" "}
          Privacy Policy
        </Link>
        .
      </span>

      <ToastContainer />
      {loading && (
        <div className={`loading-overlay ${fadeOut ? "hidden" : ""}`}>
          <img
            src="/apple-loading.gif"
            alt="Loading..."
            className="loading-spinner"
          />
        </div>
      )}
    </div>
  );
}

export default Signup;
