// frontend/src/Services/OtpInput.jsx

import React, { useState, useRef, useEffect } from 'react';

const OtpInput = ({ length = 6, onOtpSubmit = () => {}, onOtpComplete = () => {} }) => {
    const [otp, setOtp] = useState(new Array(length).fill(""));
    const inputRefs = useRef([]);

    useEffect(() => {
        if (inputRefs.current[0]) {
            inputRefs.current[0].focus();
        }
    }, []);

    useEffect(() => {
        onOtpComplete(otp.every(value => value !== ""));
    }, [otp, onOtpComplete]);

    const handleChange = (index, e) => {
        const value = e.target.value;
        if (isNaN(value) || value.length > 1) return;

        const newOtp = [...otp];
        newOtp[index] = value.slice(-1);
        setOtp(newOtp);

        // Focus on the next input field if value is entered
        if (value && index < length - 1) {
            inputRefs.current[index + 1].focus();
        }

        // Auto-submit if all fields are filled
        if (newOtp.every(digit => digit !== "")) {
            onOtpSubmit(newOtp.join(""));
        }
    };
    const moveCursorToEnd = (inputElement) => {
        if (inputElement) {
            const length = inputElement.value.length;
            inputElement.setSelectionRange(length, length);
            inputElement.focus();
        }
    };
    

    const handleKeyDown = (index, e) => {
        if (e.key === "Backspace") {
            // Move focus to the previous input if the current one is empty
            if (!otp[index] && index > 0) {
                inputRefs.current[index - 1].focus();
                moveCursorToEnd(inputRefs.current[index]);

            }
        } else if (e.key === "ArrowLeft") {
            // Move focus to the previous input field
            if (index > 0) {
                inputRefs.current[index - 1].focus();
                moveCursorToEnd(inputRefs.current[index]);
            }
        } else if (e.key === "ArrowRight") {
            // Move focus to the next input field
            if (index < length - 1) {
                inputRefs.current[index + 1].focus();
                moveCursorToEnd(inputRefs.current[index]);

            }
        } else if (e.key === "Tab") {
            // Allow tabbing to navigate between fields
            e.preventDefault(); 
            moveCursorToEnd(inputRefs.current[index]);

            if (e.shiftKey && index > 0) {
                inputRefs.current[index - 1].focus(); 
                moveCursorToEnd(inputRefs.current[index]);

            } else if (!e.shiftKey && index < length - 1) {
                inputRefs.current[index + 1].focus();
                moveCursorToEnd(inputRefs.current[index]);

            }
        } else if (e.key === "Enter") {
            // Move focus to the next input field or trigger submission
            if (index < length - 1) {
                inputRefs.current[index + 1].focus();
                moveCursorToEnd(inputRefs.current[index]);

            } else if (index === length - 1 && otp.every(value => value !== "")) {
                // Trigger submission if all fields are filled
                onOtpSubmit(otp.join(""));
                moveCursorToEnd(inputRefs.current[index]);

            }
        }
    };
    
    

    const handlePaste = (e) => {
        e.preventDefault();
        const pasteData = e.clipboardData.getData('text').slice(0, length).split("");
        const newOtp = new Array(length).fill("");

        pasteData.forEach((digit, i) => {
            if (i < length) {
                newOtp[i] = digit;
                inputRefs.current[i].value = digit; 
            }
        });

        setOtp(newOtp);
        inputRefs.current[length - 1].focus(); 
        onOtpSubmit(newOtp.join(""));
    };

    const handleClick = (index) => {
        inputRefs.current[index].focus(); // Focus on the clicked input field
        inputRefs.current[index].setSelectionRange(1, 1); 
    };

    return (
        <div className="otp-input-wrapper">
            {otp.map((value, index) => (
                <input
                    key={index}
                    type="text"
                    ref={(input) => (inputRefs.current[index] = input)}
                    value={value}
                    onChange={(e) => handleChange(index, e)}
                    onKeyDown={(e) => handleKeyDown(index, e)}
                    onPaste={(e) => handlePaste(e)}
                    onClick={() => handleClick(index)}
                    className="otpField"
                    maxLength={1}
                />
            ))}
        </div>
    );
};

export default OtpInput;
