import React, { useEffect, useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import { ToastContainer } from 'react-toastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/style/profile.css'; // Ensure you have this file

function UserProfile() {
    const [user, setUser] = useState({});
    const [loading, setLoading] = useState(true);
    const navigate = useNavigate();

    useEffect(() => {
        fetchUserProfile();
    }, []);

    const fetchUserProfile = async () => {
        setLoading(true);
        try {
            const response = await fetch('http://localhost:8080/Users/me', {
                headers: {
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                }
            });
            const result = await response.json();
            if (response.ok) {
                setUser(result);
            } else {
                handleError(result.message || 'Failed to fetch user data. Please try again.');
                navigate('/login'); // Redirect if the fetch fails
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
            navigate('/login'); // Redirect if there is a network error
        } finally {
            setLoading(false);
        }
    };

    const handleLogout = async () => {
        try {
            const response = await fetch("http://localhost:8080/auth/logout", {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${localStorage.getItem('token')}`
                },
                body: JSON.stringify({ refreshToken: localStorage.getItem('refreshToken') })
            });
            if (response.ok) {
                localStorage.removeItem('token');
                localStorage.removeItem('refreshToken');
                localStorage.removeItem('loggedInUser');
                handleSuccess('Logged out successfully! Redirecting...');
                setTimeout(() => navigate('/login'), 500);
            } else {
                handleError('Failed to log out. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    return (
        <div className="user-profile-container">
            {loading ? (
                <div className="loading-overlay-user-profile">
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            ) : (
                <>
                    <div className="user-image-name-container">
                        <div className="avatar-image-container ">
                            <img src='/avater.png' className='profilePicture' alt='Profile' />
                        </div>
                        <h1 className="user-name-text">{user.name}</h1>
                    </div>
                    <div className="profile-details-container">
                        
                        <ul className="profile-details-list">
                            <li className="profile-details-list-item"><strong className="profile-details-list-item-label">Name:</strong> {user.name}</li>
                            <li className="profile-details-list-item"><strong className="profile-details-list-item-label">Email:</strong> {user.email}</li>
                            <li className="profile-details-list-item"><strong className="profile-details-list-item-label">Username:</strong> {user.username}</li>
                            <li className="profile-details-list-item"><strong className="profile-details-list-item-label">Joined:</strong> {new Date(user.createdAt).toLocaleDateString()}</li>
                        </ul>
                    </div>
                </>
            )}
            <div className="profile-actions-container">
                <button className="profile-action-button" onClick={() => navigate(-1)}>Back</button>
                <button className="profile-action-button" onClick={handleLogout}>Logout</button>
            </div>
            <ToastContainer />
        </div>
    );
}

export default UserProfile;
