// ReactToastify.js
import { toast, Slide } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';

export const handleSuccess = (msg) => {
    toast.success(msg, {
        position: 'top-center',
        autoClose: 300000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        transition: Slide // Use the built-in Slide transition for a smooth effect
    });
}

export const handleError = (msg) => {
    toast.error(msg, {
        position: 'top-center',
        autoClose: 300000,
        hideProgressBar: true,
        closeOnClick: true,
        pauseOnHover: true,
        draggable: true,
        progress: undefined,
        transition: Slide // Use the built-in Slide transition for a smooth effect
    });
}
