import React, { useState, useEffect, useRef } from 'react';
import { FaArrowUp, FaArrowDown } from 'react-icons/fa';
import '../../../assets/style/ServicesStyle/ChatServicesStyle/ScrollButtons.css';

const ScrollButtons = ({ containerRef }) => {
  const [isScrolling, setIsScrolling] = useState(false);
  const scrollTimerRef = useRef(null);

  const scrollToTop = () => {
    containerRef.current?.scrollTo({ top: 0, behavior: 'smooth' });
  };

  const scrollToBottom = () => {
    if (containerRef.current) {
      containerRef.current.scrollTop = containerRef.current.scrollHeight;
    }
  };

  useEffect(() => {
    const container = containerRef.current;
    if (!container) return;

    const handleScroll = () => {
      setIsScrolling(true);
      clearTimeout(scrollTimerRef.current);
      scrollTimerRef.current = setTimeout(() => {
        setIsScrolling(false);
      }, 1000); // Hide scrollbar and buttons 1 second after scrolling stops
    };

    container.addEventListener('scroll', handleScroll);
    return () => {
      container.removeEventListener('scroll', handleScroll);
      clearTimeout(scrollTimerRef.current);
    };
  }, [containerRef]);

  return (
    <div className={`scroll-buttons-container ${isScrolling ? 'is-scrolling' : ''}`}>
      <div className="scroll-buttons">
        <button onClick={scrollToTop} className="scroll-button">
          <FaArrowUp />
        </button>
        <button onClick={scrollToBottom} className="scroll-button">
          <FaArrowDown />
        </button>
      </div>
    </div>
  );
};

export default ScrollButtons;
