import React, { useState } from 'react';
import '../assets/style/PromptInput.css';

const PromptInput = () => {
  const [message, setMessage] = useState('');
  const [isFocused, setIsFocused] = useState(false);

  const handleSendMessage = () => {
    console.log('Sending message:', message);
    setMessage('');
  };

  return (
    <div className={`prompt-input-container ${isFocused ? 'focused' : ''}`}>
      <div className="chat-input-section">
        <button className="add-document-icon-btn" id="add-document-btn">
          <img src="/images/icon/add-document-plus.png" alt="Add Document" />
        </button>

        <input
          type="text"
          id="message-input"
          className="message-text-input-field"
          placeholder="Send a message..."
          value={message}
          onChange={(e) => setMessage(e.target.value)}
          onFocus={() => setIsFocused(true)}
          onBlur={() => setIsFocused(false)}
          onKeyPress={(e) => {
            if (e.key === 'Enter') {
              handleSendMessage();
            }
          }}
        />

        <button
          className="send-message-btn"
          id="send-btn"
          title="Send Message"
          onClick={handleSendMessage}
          disabled={!message.trim()}
        >
          <img src="/images/icon/send-massage-up.png" alt="Send Message" />
        </button>
      </div>
    </div>
  );
};

export default PromptInput;
