import React from "react";
import "../../../assets/style/ServicesStyle/ChatServicesStyle/MessageReactions.css";

const MessageReactions = ({
  messageId,
  reaction,
  showReactions,
  onReaction,
  onToggleReactionMenu,
}) => {
  
  return (
    <div className="message-reaction-container">
      {reaction ? (
        <span
          className="reaction"
          onClick={() => onToggleReactionMenu(messageId)}
        >
          {reaction}
        </span>
      ) : (
        <button
          onClick={() => onToggleReactionMenu(messageId)}
          className="add-reaction-btn"
        >
         <svg xmlns="http://www.w3.org/2000/svg" viewBox="0 0 24 24" fill="none" stroke="currentColor" strokeWidth="2" strokeLinecap="round" strokeLinejoin="round" className="reaction-icon">
            <circle cx="12" cy="12" r="10" />
            <path d="M8 14s1.5 2 4 2 4-2 4-2" />
            <line x1="9" y1="9" x2="9.01" y2="9" />
            <line x1="15" y1="9" x2="15.01" y2="9" />
        </svg>
        </button>
      )}
      {showReactions && (
        <div className="reaction-menu">
            
          <button
            onClick={() => onReaction(messageId, "like")}
            className="reaction-btn"
          >
            <svg
              xmlns="http://www.w3.org/2000/svg"
              viewBox="0 0 16 16"
              className="reaction-icon"
            >
              <path fill="#2078F4" d="M16 8A8 8 0 1 1-.001 8 8 8 0 0 1 16 8" />
              <path
                fill="#FFFFFF"
                d="M12.162 7.338c.176.123.338.245.338.674 0 .43-.229.604-.474.725.1.163.132.36.089.546-.077.344-.392.611-.672.69.121.194.159.385.015.62-.185.295-.346.407-1.058.407H7.5c-.988 0-1.5-.546-1.5-1V7.665c0-1.23 1.467-2.275 1.467-3.13L7.361 3.47c-.005-.065.008-.224.058-.27.08-.079.301-.2.635-.2.218 0 .363.041.534.123.581.277.732.978.732 1.542 0 .271-.414 1.083-.47 1.364 0 0 .867-.192 1.879-.199 1.061-.006 1.749.19 1.749.842 0 .261-.219.523-.316.666zM3.6 7h.8a.6.6 0 0 1 .6.6v3.8a.6.6 0 0 1-.6.6h-.8a.6.6 0 0 1-.6-.6V7.6a.6.6 0 0 1 .6-.6z"
              />
            </svg>
            
          </button>



        </div>
      )}
    </div>
  );
};

export default MessageReactions;
