// ForgotChangePassword.js

import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';

function ForgotChangePassword() {
    const [otp, setOtp] = useState('');
    const [newPassword, setNewPassword] = useState('');
    const [confirmNewPassword, setConfirmNewPassword] = useState('');
    const [loading, setLoading] = useState(false);
    const navigate = useNavigate();

    const handleChangePassword = async () => {
        setLoading(true);
        try {
            const response = await fetch("http://localhost:8080/auth/forgot-change-password", {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ otp, newPassword, confirmNewPassword })
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess('Password changed successfully!');
                setTimeout(() => navigate('/login'), 500);
            } else {
                handleError(result.message || 'Failed to change password. Please try again.');
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        } finally {
            setLoading(false);
        }
    };

    return (
        <div className="forgot-change-password-container">
            <h1>Change Password</h1>
            <div className="form-group">
                <input
                    type="text"
                    id="otp"
                    placeholder="Enter your OTP..."
                    value={otp}
                    onChange={(e) => setOtp(e.target.value)}
                    required
                />
                <label htmlFor="otp" className="form-label">OTP</label>
            </div>
            <div className="form-group">
                <input
                    type="password"
                    id="newPassword"
                    placeholder="Enter your new password..."
                    value={newPassword}
                    onChange={(e) => setNewPassword(e.target.value)}
                    required
                />
                <label htmlFor="newPassword" className="form-label">New Password</label>
            </div>
            <div className="form-group">
                <input
                    type="password"
                    id="confirmNewPassword"
                    placeholder="Confirm your new password..."
                    value={confirmNewPassword}
                    onChange={(e) => setConfirmNewPassword(e.target.value)}
                    required
                />
                <label htmlFor="confirmNewPassword" className="form-label">Confirm New Password</label>
            </div>
            <button onClick={handleChangePassword} disabled={loading}>
                {loading ? 'Changing Password...' : 'Change Password'}
            </button>
            <ToastContainer />
        </div>
    );
}

export default ForgotChangePassword;
