import React, { useState, useEffect } from 'react';
import { useNavigate, useLocation } from 'react-router-dom';
import { handleSuccess, handleError, ToastContainer } from '../../utils/ReactToastify';
import '../../assets/style/ReactToastifyCustom.css';
import '../../assets/style/SentOtpForgotPassword.css';
import OtpInput from '../../Services/OtpInput';

function VerificationCodeForgotPassword() {
    const [otp, setOtp] = useState('');
    const [timer, setTimer] = useState(0);
    const [isResendEnabled, setIsResendEnabled] = useState(false);
    const [isComplete, setIsComplete] = useState(false);
    const [hasCodeBeenSent, setHasCodeBeenSent] = useState(false);
    const [attempts, setAttempts] = useState(0);
    const { state } = useLocation();
    const navigate = useNavigate();

    useEffect(() => {
        const storedAttempts = parseInt(localStorage.getItem('verificationAttempts') || '0', 10);
        setAttempts(storedAttempts);
        const storedTimer = parseInt(localStorage.getItem('verificationTimer') || '0', 10);
        if (storedTimer > 0) {
            setTimer(storedTimer);
            setHasCodeBeenSent(true);
            startCountdown(storedTimer);
        }
        return () => clearLocalStorage();
    }, []);

    const startCountdown = (duration) => {
        const countdown = setInterval(() => {
            setTimer((prev) => {
                if (prev <= 1) {
                    clearInterval(countdown);
                    setIsResendEnabled(true);
                    return 0;
                }
                localStorage.setItem('verificationTimer', (prev - 1).toString());
                return prev - 1;
            });
        }, 1000);
    };

    const handleSendOtp = async () => {
        try {
            const response = await fetch('http://localhost:8080/verification/verification-code', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                setHasCodeBeenSent(true);
                setIsResendEnabled(false);
                const newAttempts = attempts + 1;
                setAttempts(newAttempts);
                localStorage.setItem('verificationAttempts', newAttempts.toString());
                const cooldownPeriod = getCooldownPeriod(newAttempts);
                setTimer(cooldownPeriod);
                localStorage.setItem('verificationTimer', cooldownPeriod.toString());
                startCountdown(cooldownPeriod);
            } else if (response.status === 429) {
                handleError(result.message);
                setTimer(result.cooldownPeriod);
                localStorage.setItem('verificationTimer', result.cooldownPeriod.toString());
                startCountdown(result.cooldownPeriod);
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Failed to send OTP. Please try again.');
        }
    };

    const handleResendOtp = handleSendOtp;

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const response = await fetch('http://localhost:8080/verification/verify-code', {
                method: 'POST',
                headers: { 'Content-Type': 'application/json' },
                body: JSON.stringify({ email: state.email, otp }),
            });

            const result = await response.json();

            if (response.ok) {
                handleSuccess(result.message);
                clearLocalStorage();
                navigate('/forgot-password/reset-password', { state: { email: state.email } });
            } else {
                handleError(result.message);
            }
        } catch (err) {
            handleError('Network error. Please check your connection and try again.');
        }
    };

    const clearLocalStorage = () => {
        localStorage.removeItem('verificationAttempts');
        localStorage.removeItem('verificationTimer');
    };

    const getCooldownPeriod = (attempts) => {
        if (attempts < 3) return 0;
        if (attempts < 6) return 5 * 60; // 5 minutes in seconds
        return Math.min(10 + (attempts - 6) * 5, 30) * 60; // Increase by 5 minutes, max 30 minutes
    };

    return (
        <div className="otp-wrapper">
            <div>
                <img src='/images/app-icon.ico' alt='App Icon' className='app-icon' />
            </div>
            <h1 className="otp-title">Verification code</h1>
            <div className="otp-timer">
                {hasCodeBeenSent ? (
                    <p>We've sent a verification code to your registered email address.</p>
                ) : (
                    <p>Click the button to send the verification code to your email.</p>
                )}
                <div className="timer">
                    <p>Time Remaining: {timer} s</p>
                </div>
            </div>
            <form onSubmit={handleSubmit}>
                <div className="otp-buttons">
                    {!hasCodeBeenSent ? (
                        <button
                            type="button"
                            className="otp-btn send"
                            onClick={handleSendOtp}
                        >
                            Send Code
                        </button>
                    ) : timer === 0 && isResendEnabled ? (
                        <button
                            type="button"
                            className="otp-btn resend"
                            onClick={handleResendOtp}
                        >
                            Resend Code
                        </button>
                    ) : hasCodeBeenSent && timer > 0 ? (
                        <>
                            <OtpInput
                                length={6}
                                onOtpSubmit={(otp) => setOtp(otp)}
                                onOtpComplete={(complete) => setIsComplete(complete)}
                            />
                            <button
                                type="submit"
                                className={`otp-btn verify ${isComplete ? 'active' : ''}`}
                                disabled={!isComplete}
                            >
                                Verify Code
                            </button>
                        </>
                    ) : null}
                </div>
            </form>
            <ToastContainer />
        </div>
    );
}


export default VerificationCodeForgotPassword;
