import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Login() {
    const [loginInfo, setLoginInfo] = useState({
        email: '',
        password: ''
    });

    const [loading, setLoading] = useState(false);
    const [loadingVisible, setLoadingVisible] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        const copyLoginInfo = { ...loginInfo };
        copyLoginInfo[name] = value;
        setLoginInfo(copyLoginInfo);
    }

    const handleLogin = async (e) => {
        e.preventDefault();
        setLoading(true);
        setLoadingVisible(true);

        try {
            const response = await fetch('/api/auth/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(loginInfo),
            });

            const data = await response.json();

            if (data.success) {
                handleSuccess("Login Successful");
                localStorage.setItem('accessToken', data.accessToken);
                localStorage.setItem('refreshToken', data.refreshToken);
                navigate('/home');
            } else {
                handleError(data.message);
            }
        } catch (error) {
            handleError("An error occurred while logging in.");
        } finally {
            setLoading(false);
            setLoadingVisible(false);
        }
    }

    return (
        <div>
            <ToastContainer />
            {loadingVisible && <div className="loading-popup">
                <img src="/apple-loading.gif" alt="Loading" />
            </div>}
            <form onSubmit={handleLogin}>
                <input
                    type="email"
                    name="email"
                    placeholder="Email"
                    onChange={handleChange}
                    required
                />
                <input
                    type="password"
                    name="password"
                    placeholder="Password"
                    onChange={handleChange}
                    required
                />
                <button type="submit" disabled={loading}>Login</button>
            </form>
            <Link to="/signup">Sign up</Link>
        </div>
    );
}

export default Login;
