import axiosInstance from './axiosConfig';

export const fetchLoggedInUser = async () => {
    const response = await axiosInstance.get('/Users/me');
    return response.data;
};

export const signupUser = async (formData) => {
    const response = await axiosInstance.post("/auth/signup", formData, {
        headers: {
            "Content-Type": "application/json",
        },
        withCredentials: true, 
    });
    return response.data;
};

export const loginUser = async (emailOrUsername, password) => {
    const response = await axiosInstance.post("/auth/login", {
        emailOrUsername,
        password
    });
    if (response.data.success) {
        localStorage.setItem('token', response.data.accessToken);
        localStorage.setItem('refreshToken', response.data.refreshToken); // Save refresh token
    }
    return response.data;
};

export const logoutUser = async () => {
    const response = await axiosInstance.post("/auth/logout", {}, {
        headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
        withCredentials: true,
    });
    if (response.status === 200) {
        clearAuthData();
    }
    return response;
};

export const deleteUser = async (userId) => {
    const response = await axiosInstance.delete(`/auth/delete/${userId}`, {
        headers: {
            "Content-Type": "application/json",
            Authorization: `Bearer ${localStorage.getItem("token")}`,
        },
    });
    return response;
};

export const refreshToken = async () => {
    const refreshToken = localStorage.getItem('refreshToken');
    if (!refreshToken) {
        console.error('No refresh token provided');
        return false;
    }

    try {
        const response = await axiosInstance.post('/auth/refresh', {
            refreshToken: refreshToken,
        });
        if (response.status === 200) {
            localStorage.setItem('token', response.data.accessToken);
            return true;
        } else {
            clearAuthData();
            return false;
        }
    } catch (error) {
        console.error('Error refreshing token:', error);
        return false;
    }
};

export const checkAuthentication = async () => {
    const token = localStorage.getItem('token');
    if (!token) return false;
    // Optionally, you can add a token validation logic here
    return true;
};

const clearAuthData = () => {
    localStorage.removeItem('token');
    localStorage.removeItem('refreshToken');
    // Clear cookies if necessary
    const cookies = document.cookie.split("; ");
    for (let cookie of cookies) {
        const eqPos = cookie.indexOf("=");
        const name = eqPos > -1 ? cookie.substr(0, eqPos) : cookie;
        document.cookie = name + "=;expires=Thu, 01 Jan 1970 00:00:00 GMT; path=/";
    }
};
