// Login.js
import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import { ToastContainer } from 'react-toastify';
import { handleError, handleSuccess } from '../utils/ReactToastify';
import '../utils/ReactToastifyCustom.css';
import '../utils/loading.css';

function Login() {
    const [loginInfo, setLoginInfo] = useState({
        email: '',
        password: ''
    });

    const [loading, setLoading] = useState(false);
    const [loadingVisible, setLoadingVisible] = useState(false);
    const navigate = useNavigate();

    const handleChange = (e) => {
        const { name, value } = e.target;
        setLoginInfo(prev => ({ ...prev, [name]: value }));
    }

    const handleLogin = async (e) => {
        e.preventDefault();
        const { email, password } = loginInfo;
        if (!email || !password) {
            return handleError('Email and password are required');
        }
        setLoading(true);
        setLoadingVisible(true);

        try {
            const url = `http://localhost:8080/auth/login`;
            const response = await fetch(url, {
                method: "POST",
                headers: {
                    'Content-Type': 'application/json'
                },
                body: JSON.stringify(loginInfo)
            });
            const result = await response.json();
            if (response.ok) {
                handleSuccess(result.message);
                localStorage.setItem('token', result.jwtToken);
                localStorage.setItem('loggedInUser', JSON.stringify({ name: result.name, email: result.email }));
                setTimeout(() => {
                    setLoadingVisible(false);
                    setTimeout(() => {
                        navigate('/home');
                    }, 500);
                }, 1000);
            } else {
                handleError(result.message);
                setLoadingVisible(false);
            }
        } catch (err) {
            handleError(err.message || 'An error occurred');
            setLoadingVisible(false);
        } finally {
            setLoading(false);
        }
    }

    return (
        <div className='container'>
            <img src='/app-icon.ico' alt='App Icon' className='app-icon' />
            <h1>Login</h1>
            <form onSubmit={handleLogin}>
                <div className='form-group'>
                    <input
                        onChange={handleChange}
                        type='email'
                        name='email'
                        id='email'
                        placeholder='Enter your email...'
                        value={loginInfo.email}
                    />
                    <label htmlFor='email' className='form-label'>Email</label>
                </div>
                <div className='form-group'>
                    <input
                        onChange={handleChange}
                        type='password'
                        name='password'
                        id='password'
                        placeholder='Enter your password...'
                        value={loginInfo.password}
                    />
                    <label htmlFor='password' className='form-label'>Password</label>
                </div>
                <button type='submit'>Login</button>
                <Link to="/ForgotPassword" className='signup-link'>Forgot Password?</Link>
                <span>Doesn't have an account? <Link to="/signup" className='signup-link'>Signup</Link></span>
            </form>
            <ToastContainer />
            {loadingVisible && (
                <div className={`loading-overlay ${!loading ? 'hidden' : ''}`}>
                    <img src='/apple-loading.gif' alt='Loading...' className='loading-spinner' />
                </div>
            )}
        </div>
    );
}

export default Login;
